import React, { useState, useEffect } from 'react';
import { addCompany, getCompanies } from '../services/companyService'; // Import getCompanies
import { addUser } from '../services/userService';
import '../styles/SignupScreen.css';

function SignupScreen() {
    const [company, setCompany] = useState({
        name: '',
        address: '',
        phone: '',
        email: '',
    });

    const [admin, setAdmin] = useState({
        name: '',
        email: '',
        phone: '',
        password: '',
    });

    const [existingCompanies, setExistingCompanies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetch existing companies on mount
    useEffect(() => {
        const fetchCompanies = async () => {
            try {
                const data = await getCompanies();
                if (!data || Object.keys(data).length === 0) {
                    setExistingCompanies([]); // No companies found
                } else {
                    setExistingCompanies(Object.values(data)); // Convert object to array
                }
            } catch (err) {
                setError('Failed to fetch companies.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchCompanies();
    }, []);

    const handleCompanyChange = (e) => {
        const { name, value } = e.target;
        setCompany({ ...company, [name]: value });
    };

    const handleAdminChange = (e) => {
        const { name, value } = e.target;
        setAdmin({ ...admin, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newCompany = await addCompany(company);
            const adminUser = { ...admin, role: 'admin', companyId: newCompany.id };
            await addUser(adminUser);

            alert('Company and admin registered successfully!');
            setCompany({ name: '', address: '', phone: '', email: '' });
            setAdmin({ name: '', email: '', phone: '', password: '' });
        } catch (error) {
            alert('Error registering company and admin. Please try again.');
        }
    };

    return (
        <div className="signup-container">
            <h2>Register a New Company</h2>
            {isLoading ? (
                <p>Loading existing companies...</p>
            ) : error ? (
                <p className="error">{error}</p>
            ) : existingCompanies.length === 0 ? (
                <p>No existing companies found. You can register a new company below.</p>
            ) : (
                <div>
                    <h3>Existing Companies</h3>
                    <ul>
                        {existingCompanies.map((comp, index) => (
                            <li key={index}>{comp.name}</li>
                        ))}
                    </ul>
                </div>
            )}

            <form onSubmit={handleSubmit}>
                <div>
                    <label>Company Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={company.name}
                        onChange={handleCompanyChange}
                        required
                    />
                </div>
                <div>
                    <label>Address:</label>
                    <input
                        type="text"
                        name="address"
                        value={company.address}
                        onChange={handleCompanyChange}
                        required
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={company.phone}
                        onChange={handleCompanyChange}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={company.email}
                        onChange={handleCompanyChange}
                        required
                    />
                </div>

                <h3>Admin Details</h3>
                <div>
                    <label>Admin Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={admin.name}
                        onChange={handleAdminChange}
                        required
                    />
                </div>
                <div>
                    <label>Admin Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={admin.email}
                        onChange={handleAdminChange}
                        required
                    />
                </div>
                <div>
                    <label>Admin Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={admin.phone}
                        onChange={handleAdminChange}
                        required
                    />
                </div>
                <div>
                    <label>Admin Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={admin.password}
                        onChange={handleAdminChange}
                        required
                    />
                </div>

                <button type="submit">Register Company and Admin</button>
            </form>
        </div>
    );
}

export default SignupScreen;
