// notificationService.js
import { getDatabase, ref, get, set, push } from 'firebase/database';

export const fetchNotifications = async (companyId) => {
    const db = getDatabase();
    const snapshot = await get(ref(db, `notifications/${companyId}`));
    return snapshot.exists() ? snapshot.val() : {};
};

export const sendNotification = async (companyId, notificationData) => {
    const db = getDatabase();
    const notificationsRef = ref(db, `notifications/${companyId}`);
    await push(notificationsRef, notificationData);
};
