import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import BackgroundWrapper from './BackgroundWrapper';
import Loader from './Loader';

import '../styles/HomeScreen.css';
import '../styles/Navbar.css';

import { getAvailability } from '../services/availabilityService';
import { calculateWeekKey, calculateWeekRange } from '../utils/utils';

function HomeScreen() {
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();
    const [weeklyShifts, setWeeklyShifts] = useState({});

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        const fetchShifts = async () => {
            if (!userData) return;

            const companyId = userData.company.id;
            const userId = userData.user.id;

            const currentWeekKey = calculateWeekKey(0);
            const nextWeekKey = calculateWeekKey(1);

            const [currentWeekAvailability, nextWeekAvailability] = await Promise.all([
                getAvailability(companyId, currentWeekKey, userId),
                getAvailability(companyId, nextWeekKey, userId)
            ]);

            setWeeklyShifts({
                current: currentWeekAvailability,
                next: nextWeekAvailability
            });
        };

        fetchShifts();
    }, [userData]);

    if (!userData) {
        return <Loader />;
    }

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* מקום עבור ה-navbar */}

            <div className="home-container">
                <h1>Welcome, {userData.user.name}!</h1>
                <p>Company: {userData.company ? userData.company.name : "N/A"}</p>
                <div className="home-actions">
                    <button className="action-button" onClick={() => navigate('/profile')}>
                        Go to Profile
                    </button>
                </div>
            </div>
        </BackgroundWrapper>
    );
}

export default HomeScreen;
