import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginScreen from './components/LoginScreen';
import SignupScreen from './components/SignupScreen';
import HomeScreen from './components/HomeScreen';
import Navbar from './components/Navbar';
import ProfileScreen from './components/ProfileScreen';

function App() {
    const [user, setUser] = useState(null); // שומרים את פרטי המשתמש אם הוא מחובר

    useEffect(() => {
        // טיפול בשגיאה כאשר אין נתונים ב-localStorage
        try {
            const loggedUser = JSON.parse(localStorage.getItem('user'));
            if (loggedUser) {
                setUser(loggedUser);
            }
        } catch (error) {
            console.error("Error parsing user data:", error); // הצגת השגיאה במקרה של בעיה
        }
    }, []);

    return (
        <Router>
            <div>

                {user && <Navbar />} {/* מציג את ה-navbar אם יש משתמש מחובר */}
                <Routes>
                    <Route path="/" element={<LoginScreen />} />
                    <Route path="/signup" element={<SignupScreen />} />
                    <Route path="/home" element={user ? <HomeScreen /> : <LoginScreen />} />
                    <Route path="/profile" element={user ? <ProfileScreen /> : <LoginScreen />} />
                    {/* אם המשתמש לא מחובר, הניווט יפנה למסך ההתחברות */}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
