// React functions
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

// Third-party libraries
import { toast } from 'react-toastify';

// Styles
import '../styles/ChatScreen.css';
import '../styles/Navbar.css';

// Services
import { getPrivateMessages, sendPrivateMessage } from '../services/chatService';
import { getUsers } from '../services/userService';

// Internal components
import Navbar from './Navbar';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';

const PrivateChatScreen = () => {
    const { recipientId } = useParams();
    const navigate = useNavigate();

    const [userData, setUserData] = useState(null);
    const [recipient, setRecipient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    const messagesEndRef = useRef(null);

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        if (userData?.company?.id && recipientId) {
            fetchRecipient();
            fetchMessages();
        }
    }, [userData, recipientId]);

    const fetchRecipient = async () => {
        try {
            const allUsers = await getUsers();
            const found = allUsers[recipientId];
            if (found) setRecipient({ id: recipientId, ...found });
        } catch (error) {
            console.error('Error fetching recipient:', error);
        }
    };

    const fetchMessages = async () => {
        try {
            const companyId = userData.company.id;
            const user1Id = userData.user.id;
            const user2Id = recipientId;

            const data = await getPrivateMessages(companyId, user1Id, user2Id);
            const messagesArray = data ? Object.values(data).sort((a, b) => a.timestamp - b.timestamp) : [];
            setMessages(messagesArray);
        } catch (error) {
            console.error('Error fetching private messages:', error);
            toast.error('Failed to load messages.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleSendMessage = async () => {
        if (!newMessage.trim()) return;

        const messageData = {
            senderId: userData.user.id,
            senderName: userData.user.name || 'Unknown',
            content: newMessage.trim(),
            timestamp: Date.now(),
        };

        try {
            await sendPrivateMessage(userData.company.id, userData.user.id, recipientId, messageData);
            setMessages((prev) => [...prev, messageData]);
            setNewMessage('');
            scrollToBottom();
        } catch (error) {
            toast.error('Failed to send message.');
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(scrollToBottom, [messages]);

    if (!userData || !recipient) return <Loader />;

    const isSameDate = (timestamp1, timestamp2) => {
        const d1 = new Date(timestamp1);
        const d2 = new Date(timestamp2);
        return (
            d1.getFullYear() === d2.getFullYear() &&
            d1.getMonth() === d2.getMonth() &&
            d1.getDate() === d2.getDate()
        );
    };

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div>

            <div className="chat-layout">
                {/* Sidebar */}
                <div className="chat-sidebar">
                    <h3>üí¨ ◊©◊ô◊ó◊ï◊™</h3>
                    <ul>
                        <li className="chat-link" onClick={() => navigate('/chat')}>
                            üü¢ ◊¶'◊ê◊ò ◊ß◊ë◊ï◊¶◊™◊ô
                        </li>
                    </ul>
                </div>

                {/* Chat area */}
                <div className="chat-main">
                    <h1>◊¶'◊ê◊ò ◊¢◊ù {recipient.name}</h1>

                    {isLoading ? (
                        <Loader />
                    ) : (
                        <>
                            <div className="chat-messages">
                                {messages.map((msg, index) => {
                                    const currentDate = new Date(msg.timestamp);
                                    const prevDate = index > 0 ? new Date(messages[index - 1].timestamp) : null;
                                    const showDate = index === 0 || !isSameDate(currentDate, prevDate);

                                    return (
                                        <React.Fragment key={index}>
                                            {showDate && (
                                                <div className="date-header-wrapper">
                                                    <div className="date-header">
                                                        {currentDate.toLocaleDateString('he-IL', {
                                                            weekday: 'short',
                                                            day: '2-digit',
                                                            month: '2-digit',
                                                            year: 'numeric',
                                                        })}
                                                    </div>
                                                </div>
                                            )}
                                            <div className={`chat-message-wrapper ${msg.senderId === userData.user.id ? 'align-right' : 'align-left'}`}>
                                                <div className={`chat-message ${msg.senderId === userData.user.id ? 'my-message' : 'other-message'}`}>
                                                    <div className="chat-sender">{msg.senderName}</div>
                                                    <div className="chat-content">{msg.content}</div>
                                                    <div className="chat-time">
                                                        {new Date(msg.timestamp).toLocaleTimeString('he-IL', {
                                                            hour: '2-digit',
                                                            minute: '2-digit',
                                                        })}
                                                    </div>
                                                </div>
                                            </div>
                                        </React.Fragment>
                                    );
                                })}
                                <div ref={messagesEndRef} />
                            </div>

                            <div className="chat-input">
                                <input
                                    type="text"
                                    placeholder="Type a message..."
                                    value={newMessage}
                                    onChange={(e) => setNewMessage(e.target.value)}
                                    onKeyDown={handleKeyPress}
                                />
                                <button onClick={handleSendMessage}>Send</button>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </BackgroundWrapper>
    );
};

export default PrivateChatScreen;
