import React, { useEffect, useState } from 'react';
import { getCompanies } from '../services/companyService';
import '../styles/SignupScreen.css';

function SignupScreen() {
    const [companies, setCompanies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchCompanies = async () => {
            try {
                const data = await getCompanies();
                if (!data || Object.keys(data).length === 0) {
                    setCompanies([]); // אם אין נתונים, קבע מערך ריק
                } else {
                    setCompanies(Object.values(data)); // ממיר את אובייקט החברות למערך
                }
            } catch (err) {
                setError('Failed to fetch companies. Please try again later.');
            } finally {
                setIsLoading(false); // סיים את מצב הטעינה
            }
        };

        fetchCompanies();
    }, []);

    return (
        <div className="signup-container">
            <h2>Register a New Company</h2>
            {isLoading ? (
                <p>Loading companies...</p> // הצגת הודעת טעינה
            ) : error ? (
                <p className="error">{error}</p> // הצגת הודעת שגיאה
            ) : companies.length === 0 ? (
                <p>No companies found.</p> // הודעה על DB ריק
            ) : (
                <ul>
                    {companies.map((company, index) => (
                        <li key={index}>{company.name}</li> // הצגת שמות החברות
                    ))}
                </ul>
            )}

            <form>
                {/* שאר הטופס להוספת חברה */}
            </form>
        </div>
    );
}

export default SignupScreen;
