import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import BackgroundWrapper from './BackgroundWrapper';
import Loader from './Loader';

import '../styles/HomeScreen.css';
import '../styles/Navbar.css';

import { getAvailability } from '../services/availabilityService';
import { calculateWeekKey, calculateWeekRange } from '../utils/utils';

function HomeScreen() {
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();
    const [weeklyShifts, setWeeklyShifts] = useState({});

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        const fetchShifts = async () => {
            if (!userData) return;

            const companyId = userData.company.id;
            const userId = userData.user.id;

            const currentWeekKey = calculateWeekKey(0);
            const nextWeekKey = calculateWeekKey(1);

            const [currentWeekAvailability, nextWeekAvailability] = await Promise.all([
                getAvailability(companyId, currentWeekKey, userId),
                getAvailability(companyId, nextWeekKey, userId)
            ]);

            setWeeklyShifts({
                current: currentWeekAvailability,
                next: nextWeekAvailability
            });
        };

        fetchShifts();
    }, [userData]);

    const renderWeeklySchedule = (availability) => {
        const shifts = ['Morning', 'Noon', 'Evening', 'Night'];
        const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

        return (
            <table className="mini-schedule-table">
                <thead>
                    <tr>
                        <th>Shift</th>
                        {days.map(day => <th key={day}>{day.slice(0, 3)}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {shifts.map(shift => (
                        <tr key={shift}>
                            <td>{shift[0] + (shift === 'Night' ? 't' : '')}</td>
                            {days.map(day => {
                                const status = availability?.[shift]?.[day]?.status;
                                if (status === 'selected') return <td key={day}>‚úÖ</td>;
                                if (availability?.[shift]?.[day]?.isAvailable) return <td key={day}>üü†</td>;
                                return <td key={day}></td>;
                            })}
                        </tr>
                    ))}
                </tbody>
            </table>
        );
    };

    if (!userData) {
        return <Loader />;
    }

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* ◊û◊ß◊ï◊ù ◊¢◊ë◊ï◊® ◊î-navbar */}

            <div className="home-container">
                <h1>Welcome, {userData.user.name}!</h1>
                <p>Company: {userData.company ? userData.company.name : "N/A"}</p>
                <div className="weekly-schedules">
                    <h2>Your Weekly Schedule</h2>
                    <h3>This Week</h3>
                    {weeklyShifts.current ? renderWeeklySchedule(weeklyShifts.current) : <p>Loading current week...</p>}
                    <h3>Next Week</h3>
                    {weeklyShifts.next ? renderWeeklySchedule(weeklyShifts.next) : <p>Loading next week...</p>}
                </div>

                <div className="home-actions">
                    <button className="action-button" onClick={() => navigate('/profile')}>
                        Go to Profile
                    </button>
                </div>
            </div>
        </BackgroundWrapper>
    );
}

export default HomeScreen;
