import React, { useState, useEffect } from 'react';
import { getUsers, deleteUser, addUser } from '../services/userService';
import Navbar from '../components/Navbar';
import '../styles/EmployeesScreen.css';

function EmployeesScreen() {
    const [employees, setEmployees] = useState([]);
    const [newEmployee, setNewEmployee] = useState({ name: '', email: '', phone: '', role: 'employee' });
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const data = await getUsers();
                const usersWithIds = data ? Object.entries(data).map(([id, user]) => ({ id, ...user })) : [];
                setEmployees(usersWithIds);
            } catch (err) {
                setError('Failed to fetch employees.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchEmployees();
    }, []);

    const handleDelete = async (id) => {
        try {
            await deleteUser(id);
            setEmployees(employees.filter((employee) => employee.id !== id));
        } catch (err) {
            alert('Failed to delete employee.');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewEmployee((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            // חילוץ פרטי המשתמש המחובר מתוך localStorage
            const loggedUser = JSON.parse(localStorage.getItem('user'));
            if (!loggedUser || !loggedUser.company) {
                alert('Unable to add employee: No company information found.');
                return;
            }

            // יצירת המשתמש החדש
            const addedEmployee = await addUser(newEmployee);

            // עדכון רשימת החברות של המשתמש החדש עם פרטי החברה הנוכחית
            await updateUserCompanies(addedEmployee, loggedUser.company);

            // עדכון רשימת העובדים ב-state
            const updatedEmployee = {
                ...addedEmployee,
                companyIds: [
                    ...(addedEmployee.companyIds || []),
                    {
                        companyId: loggedUser.company.id,
                        companyName: loggedUser.company.name,
                    },
                ],
            };

            setEmployees((prev) => [...prev, updatedEmployee]);

            // איפוס הטופס
            setNewEmployee({ name: '', email: '', phone: '', role: 'employee' });
        } catch (err) {
            console.error('Error adding employee:', err.message);
            alert('Failed to add employee.');
        }
    };


    return (
        <div>
            <Navbar />
            <div className="employees-container">
                <h2>Employees</h2>

                {isLoading ? (
                    <p>Loading employees...</p>
                ) : error ? (
                    <p className="error">{error}</p>
                ) : (
                    <table className="employees-table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Role</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {employees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.phone}</td>
                                    <td>{employee.role}</td>
                                    <td>
                                        <button className="action-button delete" onClick={() => handleDelete(employee.id)}>
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                <form className="add-employee-form" onSubmit={handleAddEmployee}>
                    <h3>Add New Employee</h3>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={newEmployee.name}
                        onChange={handleChange}
                        required
                    />
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={newEmployee.email}
                        onChange={handleChange}
                        required
                    />
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={newEmployee.phone}
                        onChange={handleChange}
                        required
                    />
                    <label>Role:</label>
                    <select name="role" value={newEmployee.role} onChange={handleChange}>
                        <option value="employee">Employee</option>
                        <option value="admin">Admin</option>
                    </select>
                    <button type="submit">Add Employee</button>
                </form>
            </div>
        </div>
    );
}

export default EmployeesScreen;
