import React, { useState, useEffect } from 'react';
import { getUsers, deleteUser, addUser, updateUserCompanies } from '../services/userService';
import { addEmployeeToCompany } from '../services/companyService';
import Navbar from '../components/Navbar';
import '../styles/EmployeesScreen.css';

function EmployeesScreen() {
    const [employees, setEmployees] = useState([]);
    const [newEmployee, setNewEmployee] = useState({ name: '', email: '', phone: '', role: 'employee', password: '' });
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const loggedUser = JSON.parse(localStorage.getItem('user'));
                const companyId = loggedUser?.company?.id;

                if (!companyId) {
                    setError('No company information found.');
                    return;
                }

                const data = await getUsers();
                const usersWithIds = data ? Object.entries(data).map(([id, user]) => ({ id, ...user })) : [];
                const filteredEmployees = usersWithIds.filter((user) =>
                    user.companyIds?.some((company) => company.companyId === companyId)
                );

                setEmployees(filteredEmployees);
            } catch (err) {
                setError('Failed to fetch employees.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEmployees();
    }, []);

    const handleDelete = async (id) => {
        try {
            await deleteUser(id);
            setEmployees(employees.filter((employee) => employee.id !== id));
        } catch (err) {
            alert('Failed to delete employee.');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewEmployee((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            const loggedUser = JSON.parse(localStorage.getItem('user'));
            const company = loggedUser?.company;

            if (!company) {
                alert('No company information found. Please try again.');
                return;
            }

            // Check if the email is already used
            const existingUser = employees.find((user) => user.email.toLowerCase() === newEmployee.email.toLowerCase());

            if (existingUser) {
                alert('An employee with this email already exists.');
                return;
            }

            // Add the new employee
            const addedEmployee = await addUser({ ...newEmployee });

            // Update the employee's companies list
            await updateUserCompanies(addedEmployee, company);

            // Add the employee to the company's employee list
            await addEmployeeToCompany(company.id, addedEmployee.id);

            // Update the state with the new employee
            setEmployees((prev) => [...prev, { id: addedEmployee.id, ...addedEmployee }]);

            // Reset the form fields
            setNewEmployee({ name: '', email: '', phone: '', role: 'employee', password: '' });
        } catch (err) {
            alert('Failed to add employee.');
            console.error(err);
        }
    };

    return (
        <div>
            <Navbar />
            <div className="employees-container">
                <h2>Employees</h2>

                {isLoading ? (
                    <p>Loading employees...</p>
                ) : error ? (
                    <p className="error">{error}</p>
                ) : (
                    <table className="employees-table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Role</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {employees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.phone}</td>
                                    <td>{employee.role}</td>
                                    <td>
                                        <button className="action-button delete" onClick={() => handleDelete(employee.id)}>
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                <form className="add-employee-form" onSubmit={handleAddEmployee}>
                    <h3>Add New Employee</h3>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={newEmployee.name}
                        onChange={handleChange}
                        required
                    />
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={newEmployee.email}
                        onChange={handleChange}
                        required
                    />
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={newEmployee.phone}
                        onChange={handleChange}
                        required
                    />
                    <label>Role:</label>
                    <select name="role" value={newEmployee.role} onChange={handleChange}>
                        <option value="employee">Employee</option>
                        <option value="admin">Admin</option>
                    </select>
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={newEmployee.password}
                        onChange={handleChange}
                        required
                    />
                    <button type="submit">Add Employee</button>
                </form>
            </div>
        </div>
    );
}

export default EmployeesScreen;
