import React, { useState, useEffect } from 'react';
import { getAllAvailabilities } from '../services/availabilityService';
import { getUsers } from '../services/userService';
import { calculateWeekKey, calculateWeekRange, getWeekDates } from '../utils/utils';
import { updateAvailabilityStatus } from '../services/availabilityService';
import Navbar from '../components/Navbar';
import '../styles/ScheduleScreen.css';

function ScheduleScreen() {
    const [availability, setAvailability] = useState({});
    const [users, setUsers] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [weekOffset, setWeekOffset] = useState(0); // Offset לשבוע הנוכחי
    const [currentCompany, setCurrentCompany] = useState(null);

    useEffect(() => {
        const fetchAvailabilityAndUsers = async () => {
            try {
                const loggedUser = JSON.parse(localStorage.getItem('user'));
                const companyId = loggedUser?.company?.id;

                if (!companyId) {
                    setError('No company information found.');
                    return;
                }

                setCurrentCompany(companyId);

                const weekKey = calculateWeekKey(weekOffset);

                // שליפת זמינות ומשתמשים
                const [availabilityData, usersData] = await Promise.all([
                    getAllAvailabilities(companyId, weekKey),
                    getUsers()
                ]);

                setAvailability(availabilityData || {});
                setUsers(usersData || {});
            } catch (err) {
                setError('Failed to fetch data.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchAvailabilityAndUsers();
    }, [weekOffset]);

    const handleWeekChange = (direction) => {
        setWeekOffset((prevOffset) => prevOffset + direction);
    };

    const getStatusClass = (status) => {
        switch (status) {
            case 'selected':
                return 'status-selected';
            case 'disabled':
                return 'status-disabled';
            case 'default':
            default:
                return 'status-default';
        }
    };

    const handleStatusUpdate = async (userId, shift, day, currentStatus) => {
        if (currentStatus === 'disabled') return; // Disable click for disabled status

        try {
            const weekKey = calculateWeekKey(weekOffset);
            const newStatus = currentStatus === 'default' ? 'selected' : 'default';

            const updatedAvailability = { ...availability };

            // Update the current shift's status to 'selected' or 'default'
            updatedAvailability[userId][shift][day].status = newStatus;
            await updateAvailabilityStatus(currentCompany, weekKey, userId, shift, day, newStatus);

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error('Failed to update status:', error);
        }
    };

    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* רווח לניווט */}

            <div className="schedule-container">
                <h2>Weekly Schedule</h2>
                <div className="week-navigation">
                    <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                    <span className="week-range">{calculateWeekRange(weekOffset)}</span>
                    <button onClick={() => handleWeekChange(1)}>Next Week</button>
                </div>

                {isLoading ? (
                    <p>Loading schedule...</p>
                ) : error ? (
                    <p className="error">{error}</p>
                ) : (
                    <table className="schedule-table">
                        <thead>
                            <tr>
                                <th>Shift</th>
                                {getWeekDates(weekOffset).map(({ dayName, date }) => (
                                    <th key={dayName}>
                                        {dayName} ({date})
                                    </th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {['Morning', 'Noon', 'Evening', 'Night'].map((shift) => (
                                <tr key={shift}>
                                    <td>{shift}</td>
                                    {getWeekDates(weekOffset).map(({ dayName }) => (
                                        <td key={dayName}>
                                            {Object.entries(availability).map(([userId, userData]) => {
                                                const availabilityInfo = userData?.[shift]?.[dayName];
                                                if (availabilityInfo?.isAvailable) {
                                                    const statusClass = getStatusClass(availabilityInfo.status);
                                                    return (
                                                        <div
                                                            key={userId}
                                                            className={`availability-entry ${statusClass}`}
                                                            onClick={() =>
                                                                handleStatusUpdate(
                                                                    userId,
                                                                    shift,
                                                                    dayName,
                                                                    availabilityInfo.status
                                                                )
                                                            }
                                                        >
                                                            {users[userId]?.name || 'Unknown'}
                                                        </div>
                                                    );
                                                }
                                                return null;
                                            })}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default ScheduleScreen;
