import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import BackgroundWrapper from './BackgroundWrapper';
import Loader from './Loader';

import '../styles/HomeScreen.css';
import '../styles/Navbar.css';

import { getAvailability } from '../services/availabilityService';
import { calculateWeekKey } from '../utils/utils';

function HomeScreen() {
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();
    const [scheduleByDate, setScheduleByDate] = useState({ current: [], next: [] });

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        const fetchShifts = async () => {
            if (!userData) return;

            const companyId = userData.company.id;
            const userId = userData.user.id;
            const weekOffsets = [0, 1];
            const allWeeks = {};

            const shifts = ['Morning', 'Noon', 'Evening', 'Night'];
            const shiftTimes = {
                Morning: '06:00 - 14:00',
                Noon: '14:00 - 22:00',
                Evening: '22:00 - 06:00',
                Night: '22:00 - 06:00',
            };

            const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

            for (let offset of weekOffsets) {
                const weekKey = calculateWeekKey(offset);
                const availability = await getAvailability(companyId, weekKey, userId);

                const startOfWeek = new Date();
                startOfWeek.setDate(startOfWeek.getDate() + offset * 7 - startOfWeek.getDay());

                const formatted = [];

                days.forEach((day, i) => {
                    const date = new Date(startOfWeek);
                    date.setDate(startOfWeek.getDate() + i);
                    const dateStr = date.toLocaleDateString('he-IL');

                    shifts.forEach((shift) => {
                        const status = availability?.[shift]?.[day]?.status;
                        if (status === 'selected') {
                            formatted.push({
                                date: dateStr,
                                day,
                                shift,
                                time: shiftTimes[shift],
                            });
                        }
                    });
                });

                allWeeks[offset === 0 ? 'current' : 'next'] = formatted;
            }

            setScheduleByDate(allWeeks);
        };

        fetchShifts();
    }, [userData]);

    if (!userData) {
        return <Loader />;
    }

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* מקום עבור ה-navbar */}

            <div className="home-container">
                <h1>Welcome, {userData.user.name}!</h1>
                <p>Company: {userData.company ? userData.company.name : "N/A"}</p>
                <div className="home-actions">
                    <button className="action-button" onClick={() => navigate('/profile')}>
                        Go to Profile
                    </button>
                </div>
            </div>
        </BackgroundWrapper>
    );
}

export default HomeScreen;
