        return shifts.map((shift) => {
            const row = { shift: shift[0] + (shift === 'Night' ? 't' : '') }; // קיצור לשם המשמרת
            days.forEach((day) => {
                const shiftData = availability[userId]?.[shift]?.[day];
                if (shiftData?.status === 'selected') {
                    row[day] = '✅'; // שובץ
                } else if (shiftData?.isAvailable) {
                    row[day] = '🟠'; // רק זמינות (חלול)
                } else {
                    row[day] = ''; // לא הגיש זמינות
                }
            });
            return row;
        });
    };

    const handleOfferShift = async () => {
        const weekKey = calculateWeekKey(weekOffset);
        const shift = selectedShiftToSwap.shift;
        const day = selectedShiftToSwap.day;
        const fromUser = selectedShiftToSwap.userId;
        const weekRange = calculateWeekRange(weekOffset);

        let recipients = [];

        if (offerToUserId) {
            // נבחר עובד ספציפי - בדיקה אם הוא עומד בתנאים
            const currentShiftCount = shiftCounts[offerToUserId] || 0;
            const maxShiftsAllowed = weeklyShiftTargets[offerToUserId] || 6;

            if (currentShiftCount >= maxShiftsAllowed) {
                toast.error(`${users[offerToUserId]?.name || 'This employee'} already has ${currentShiftCount} shifts this week (max allowed is ${maxShiftsAllowed}).`);
                return;
            }

            const isAssignedThatDay = Object.values(availability?.[offerToUserId] || {}).some(
                shiftData => shiftData?.[day]?.status === 'selected'
            );
            if (isAssignedThatDay) {
                toast.error(`${users[offerToUserId]?.name || 'This employee'} is already assigned to a shift on ${day}.`);
                return;
            }

            const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
            const currentIndex = days.indexOf(day);
            const prev = days[currentIndex - 1];
            const next = days[currentIndex + 1];

            if (
                ['Morning', 'Noon'].includes(shift) &&
                prev &&
                availability?.[offerToUserId]?.Night?.[prev]?.status === 'selected'
            ) {
                toast.error(`${users[offerToUserId]?.name || 'This employee'} cannot take a morning/noon shift after a night shift.`);
                return;
            }

            if (
                shift === 'Night' &&
                next &&
                (
                    availability?.[offerToUserId]?.Morning?.[next]?.status === 'selected' ||
                    availability?.[offerToUserId]?.Noon?.[next]?.status === 'selected'
                )
            ) {
                toast.error(`${users[offerToUserId]?.name || 'This employee'} cannot take a night shift before a morning/noon shift.`);
                return;
            }

            recipients.push(offerToUserId);
        } else {
            // "No one specific" - שלח לכל מי שעובר את הסינונים
            recipients = Object.entries(users)
                .filter(([id, user]) => {
                    if (id === fromUser) return false;

                    const currentShiftCount = shiftCounts[id] || 0;
                    const maxShiftsAllowed = weeklyShiftTargets[id] || 6;
                    if (currentShiftCount >= maxShiftsAllowed) return false;

                    const isAssignedThatDay = Object.values(availability?.[id] || {}).some(
                        shiftData => shiftData?.[day]?.status === 'selected'
                    );
                    if (isAssignedThatDay) return false;

                    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
                    const currentIndex = days.indexOf(day);
                    const prev = days[currentIndex - 1];
                    const next = days[currentIndex + 1];

                    if (
                        ['Morning', 'Noon'].includes(shift) &&
                        prev &&
                        availability?.[id]?.Night?.[prev]?.status === 'selected'
                    ) return false;

                    if (
                        shift === 'Night' &&
                        next &&
                        (
                            availability?.[id]?.Morning?.[next]?.status === 'selected' ||
                            availability?.[id]?.Noon?.[next]?.status === 'selected'
                        )
                    ) return false;

                    return true;
                })
                .map(([id]) => id);
        }

        if (recipients.length === 0) {
            toast.info("No eligible employees found to offer the shift to.");
            return;
        }

        for (const recipientId of recipients) {
            const link = `/schedule?weekOffset=${weekOffset}&offerShift=true&offerDay=${day}&shift=${shift}&fromUser=${fromUser}&offeredTo=${recipientId}`;

            await sendNotification(
                currentCompany,
                recipientId,
                `You are offered to take ${shift} shift on ${day} (Week: ${weekRange}) from ${users[fromUser]?.name || 'a colleague'}.`,
                link
            );
        }

        toast.success('Shift offer sent successfully!');
        setSelectedShiftToSwap(null);
        setOfferToUserId('');
    };


    useEffect(() => {
        if (hasCheckedShiftOffer || isLoading || !loggedInUser) return; // ← חשוב!

        const offerShift = queryParams.get('offerShift');
