import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';

import {
    addCompany,
    getCompanies,
    addEmployeeToCompany,
    addAdminToCompany,
} from '../services/companyService';
import { addUser, updateUserCompanies, getUsers } from '../services/userService';
import { FaBuilding, FaEnvelope, FaLock, FaPhone, FaUser } from 'react-icons/fa'; // אייקונים
import '../styles/SignupScreen.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function SignupScreen() {
    const navigate = useNavigate();
    const [company, setCompany] = useState({ name: '', address: '', phone: '', email: '' });
    const [admin, setAdmin] = useState({ name: '', email: '', phone: '', password: '' });
    const [existingCompanies, setExistingCompanies] = useState([]);
    const [existingUsers, setExistingUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    const fileInputRef = useRef(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const companiesData = await getCompanies();
                setExistingCompanies(companiesData ? Object.values(companiesData) : []);

                const usersData = await getUsers();
                const usersWithIds = usersData
                    ? Object.entries(usersData).map(([id, user]) => ({ id, ...user }))
                    : [];
                setExistingUsers(usersWithIds);
            } catch (err) {
                setError('Failed to fetch data.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleChange = (setter) => (e) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // בדוק אם החברה קיימת
            if (checkIfCompanyExists()) {
                setError('A company with this name already exists. Please choose a different name.');
                toast.error('A company with this name already exists. Please choose a different name.', {
                    autoClose: 2000,
                });
                return;
            }

            // בדוק אם המשתמש קיים
            const existingUser = await handleExistingUser();

            let newCompany, adminUserId;

            if (existingUser) {
                newCompany = await addCompany(company);
                await updateUserCompanies(existingUser, newCompany);
                adminUserId = existingUser.id;
            } else {
                // אם המשתמש לא קיים, צור חברה חדשה
                const result = await handleNewCompanyAndAdmin();
                newCompany = result.newCompany;
                adminUserId = result.adminUserId;
            }

            // הוסף את המנהל לחברה
            await addAdminToCompany(newCompany.id, adminUserId);

            toast.success('Company and admin registered successfully!', {
                autoClose: 2000,
                onClose: () => navigate('/'),
            });
        } catch (error) {
            console.error('Error registering company and admin:', error);
            setError('Error registering company and admin. Please try again.');
            toast.error('Error registering company and admin. Please try again.', {
                autoClose: 2000,
            });
        }
    };

    const checkIfCompanyExists = () => {
        return existingCompanies.some(
            (comp) => comp.name.toLowerCase() === company.name.toLowerCase()
        );
    };

    const handleExistingUser = async () => {
        const existingUser = existingUsers.find(
            (user) => user.email.toLowerCase() === admin.email.toLowerCase()
        );
        return existingUser;
    };

    const handleNewCompanyAndAdmin = async () => {
        let newCompany = await addCompany(company);
        let photoDataUrl = await handleProfilePhoto();
        const adminUser = {
            ...admin,
            role: 'admin',
            companyId: newCompany.id,
            photoData: photoDataUrl,
        };
        const addedUser = await addUser(adminUser);
        await addEmployeeToCompany(newCompany.id, addedUser.id);
        return { newCompany, adminUserId: addedUser.id };
    };


    return (
        <BackgroundWrapper >

            <div className="signup-container">
                <h1>Register a New Company</h1>
                {isLoading ? (
                    <Loader />
                ) : error ? (
                    <p className="error">{error}</p>
                ) : null}

                <form onSubmit={handleSubmit} className="signup-form">
                    <h3>Company Details</h3>
                    <div className="form-group">
                        <label>Company Name:</label>
                        <FaBuilding className="signup-input-icon" />
                        <input
                            type="text"
                            name="name"
                            value={company.name}
                            onChange={handleChange(setCompany)}
                            required
                            placeholder="Enter company name"
                        />
                    </div>
                    <div className="form-group">
                        <label>Address:</label>
                        <FaBuilding className="signup-input-icon" />
                        <input
                            type="text"
                            name="address"
                            value={company.address}
                            onChange={handleChange(setCompany)}
                            required
                            placeholder="Enter company address"
                        />
                    </div>
                    <div className="form-group">
                        <label>Phone:</label>
                        <FaPhone className="signup-input-icon" />
                        <input
                            type="text"
                            name="phone"
                            value={company.phone}
                            onChange={handleChange(setCompany)}
                            required
                            placeholder="Enter company phone"
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <FaEnvelope className="signup-input-icon" />
                        <input
                            type="email"
                            name="email"
                            value={company.email}
                            onChange={handleChange(setCompany)}
                            required
                            placeholder="Enter company email"
                        />
                    </div>

                    <h3>Admin Details</h3>
                    <div className="form-group">
                        <label>Name:</label>
                        <FaUser className="signup-input-icon" />
                        <input
                            type="text"
                            name="name"
                            value={admin.name}
                            onChange={handleChange(setAdmin)}
                            required
                            placeholder="Enter admin name"
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <FaEnvelope className="signup-input-icon" />
                        <input
                            type="email"
                            name="email"
                            value={admin.email}
                            onChange={handleChange(setAdmin)}
                            required
                            placeholder="Enter admin email"
                        />
                    </div>
                    <div className="form-group">
                        <label>Phone:</label>
                        <FaPhone className="signup-input-icon" />
                        <input
                            type="text"
                            name="phone"
                            value={admin.phone}
                            onChange={handleChange(setAdmin)}
                            required
                            placeholder="Enter admin phone"
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <FaLock className="signup-input-icon" />
                        <input
                            type="password"
                            name="password"
                            value={admin.password}
                            onChange={handleChange(setAdmin)}
                            required
                            placeholder="Enter admin password"
                        />
                    </div>
                    <div className="form-group">
                        <label>Profile Photo:</label>
                        <input
                            type="file"
                            accept="image/*"
                            ref={fileInputRef}
                            onChange={(e) => setAdmin((prev) => ({ ...prev, photoFile: e.target.files[0] }))}
                        />

                    </div>
                    {admin.photoFile && (
                        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'start' }}>
                            <img
                                src={URL.createObjectURL(admin.photoFile)}
                                alt="Preview"
                                style={{
                                    width: '100px',
                                    height: '100px',
                                    objectFit: 'cover',
                                    borderRadius: '8px',
                                    margin: '10px 0'
                                }}
                            />
                            <button
                                type="button"
                                className="secondary-button"
                                onClick={() => {
                                    fileInputRef.current.value = ''; // אפס את התצוגה של שם הקובץ
                                    setAdmin((prev) => {
                                        const updated = { ...prev };
                                        delete updated.photoFile;
                                        return updated;
                                    });
                                }}
                            >
                                Remove Photo
                            </button>

                        </div>
                    )}

                    <button type="submit" className="primary-button">Register Company and Admin</button>
                    <button
                        type="button"
                        className="link-button"
                        onClick={() => navigate('/')}
                    >
                        Back to Login
                    </button>
                </form>

                <ToastContainer />
            </div>
        </BackgroundWrapper>

    );
}

export default SignupScreen;
