// Import modal component and CSS for styling
import Modal from 'react-modal';
import '../../styles/EditProfileModalCard.css';

import { isValidName, isValidEmail, isValidPhone } from '../../utils/validationUtils';

// EditProfileModal component for editing user profile details
const EditProfileModal = ({ isOpen, onClose, profileUser, setProfileUser, onSubmit, title }) => {

    const handleSubmit = (e) => {
        e.preventDefault();

        const { name, email, phone } = profileUser;

        const errors = [];
        if (!isValidName(name)) errors.push('Invalid name.');
        if (!isValidEmail(email)) errors.push('Invalid email address.');
        if (!isValidPhone(phone)) errors.push('Invalid phone number.');

        if (errors.length > 0) {
            alert(errors.join('\n'));
            return;
        }

        // Call parent onSubmit if validation passes
        onSubmit(e);
    };

    // Removes the profile photo from the state
    const handleRemovePhoto = () => {
        setProfileUser(prev => ({
            ...prev,
            photoData: '',
            photoFile: null
        }));
    };

    return (
        // Modal container setup
        <Modal
            isOpen={isOpen} // Controls modal visibility
            onRequestClose={onClose} // Allows closing by clicking outside or pressing ESC
            className="modal" // Modal content CSS class
            overlayClassName="modal-overlay" // Modal background CSS class
        >
            {/* Modal title */}
            <h3>{title}</h3>
            {/* Form to edit profile details */}
            <form className="addOrEdit-employee-form" onSubmit={onSubmit}>
                {/* Name input */}
                <label>Name:</label>
                <input
                    type="text"
                    value={profileUser.name}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, name: e.target.value }))}
                    required
                />
                {/* Email input */}
                <label>Email:</label>
                <input
                    type="email"
                    value={profileUser.email}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, email: e.target.value }))}
                    required
                />
                {/* Phone input */}
                <label>Phone:</label>
                <input
                    type="text"
                    value={profileUser.phone || ''}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, phone: e.target.value }))}
                    required
                />
                {/* Role selection dropdown */}
                <label>Role:</label>
                <select
                    value={profileUser.role}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, role: e.target.value }))}
                >
                    <option value="employee">Employee</option>
                    <option value="admin">Admin</option>
                </select>

                {/* Profile photo file input */}
                <label>Profile Photo:</label>
                <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setProfileUser(prev => ({ ...prev, photoFile: e.target.files[0] }))}
                />

                {/* Preview and remove photo button */}
                {(profileUser.photoFile || profileUser.photoData) && (
                    <div className="profile-photo-wrapper">
                        <img
                            src={profileUser.photoFile ? URL.createObjectURL(profileUser.photoFile) : profileUser.photoData}
                            alt="Preview"
                            className="employee-photo-horizontal"
                        />
                        <button type="button" onClick={handleRemovePhoto} className="remove-photo-button">
                            Remove Photo
                        </button>
                    </div>
                )}

                {/* Submit and cancel buttons */}
                <button type="submit">Save Changes</button>
                <button type="button" onClick={onClose} className="cancel-button">
                    Cancel
                </button>
            </form>
        </Modal>
    );
};

export default EditProfileModal;
