import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    addCompany,
    addEmployeeToCompany,
    addAdminToCompany,
} from '../services/companyService';
import { addUser } from '../services/userService';
import '../styles/SignupScreen.css';

function SignupScreen() {
    const navigate = useNavigate();
    const [company, setCompany] = useState({ name: '', address: '', phone: '', email: '' });
    const [admin, setAdmin] = useState({ name: '', email: '', phone: '', password: '' });

    const handleChange = (setter) => (e) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newCompany = await addCompany(company);
            const adminUser = { ...admin, role: 'admin', companyId: newCompany.id };
            const addedUser = await addUser(adminUser);
            await addEmployeeToCompany(newCompany.id, addedUser.id);
            await addAdminToCompany(newCompany.id, addedUser.id);
            alert('Company and admin registered successfully!');
            navigate('/');
        } catch (error) {
            alert('Error registering company and admin. Please try again.');
        }
    };

    return (
        <div className="signup-container">
            <h2>Register a New Company</h2>
            <form onSubmit={handleSubmit} className="signup-form">
                <h3>Company Details</h3>
                <div className="form-group">
                    <label>Company Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={company.name}
                        onChange={handleChange(setCompany)}
                        required
                        placeholder="Enter company name"
                    />
                </div>
                <div className="form-group">
                    <label>Address:</label>
                    <input
                        type="text"
                        name="address"
                        value={company.address}
                        onChange={handleChange(setCompany)}
                        required
                        placeholder="Enter company address"
                    />
                </div>
                <div className="form-group">
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={company.phone}
                        onChange={handleChange(setCompany)}
                        required
                        placeholder="Enter company phone"
                    />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={company.email}
                        onChange={handleChange(setCompany)}
                        required
                        placeholder="Enter company email"
                    />
                </div>

                <h3>Admin Details</h3>
                <div className="form-group">
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={admin.name}
                        onChange={handleChange(setAdmin)}
                        required
                        placeholder="Enter admin name"
                    />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={admin.email}
                        onChange={handleChange(setAdmin)}
                        required
                        placeholder="Enter admin email"
                    />
                </div>
                <div className="form-group">
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={admin.phone}
                        onChange={handleChange(setAdmin)}
                        required
                        placeholder="Enter admin phone"
                    />
                </div>
                <div className="form-group">
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={admin.password}
                        onChange={handleChange(setAdmin)}
                        required
                        placeholder="Enter admin password"
                    />
                </div>

                <button type="submit" className="primary-button">Register Company and Admin</button>
                <button
                    type="button"
                    className="link-button"
                    onClick={() => navigate('/')}
                >
                    Back to Login
                </button>
            </form>
        </div>
    );
}

export default SignupScreen;
