import React, { useEffect, useState } from 'react';
import { getUserNotifications, markAsRead } from '../services/notificationService';
import { ThreeDots } from 'react-loader-spinner';
import Loader from './Loader';

import Navbar from './Navbar'; // הוספת שורת ייבוא
import '../styles/Navbar.css'; // אם יש קובץ CSS ייעודי
import '../styles/NotificationsScreen.css'; // אם יצרת סגנונות נוספים למסך זה

function NotificationsScreen() {
    const [notifications, setNotifications] = useState([]);
    const [loading, setLoading] = useState(true);

    const user = JSON.parse(localStorage.getItem('user'));
    const companyId = user?.company?.id;
    const userId = user?.user?.id;

    useEffect(() => {
        const fetchNotifications = async () => {
            try {
                const data = await getUserNotifications(companyId, userId);
                const formatted = data ? Object.entries(data).map(([id, notif]) => ({
                    id,
                    ...notif
                })) : [];
                setNotifications(formatted.reverse());
            } catch (error) {
                console.error('Error loading notifications:', error);
            } finally {
                setLoading(false);
            }
        };

        if (companyId && userId) fetchNotifications();
    }, [companyId, userId]);

    const handleMarkAsRead = async (notifId) => {
        try {
            await markAsRead(companyId, userId, notifId);
            setNotifications(prev =>
                prev.map(n => n.id === notifId ? { ...n, read: true } : n)
            );
        } catch (error) {
            console.error('Failed to mark as read:', error);
        }
    };

    const handleMarkAllAsRead = async () => {
        try {
            const unreadNotifs = notifications.filter(n => !n.read);
            await Promise.all(
                unreadNotifs.map(n => markAsRead(companyId, userId, n.id))
            );
            setNotifications(prev =>
                prev.map(n => ({ ...n, read: true }))
            );
        } catch (error) {
            console.error('Failed to mark all as read:', error);
        }
    };


    if (loading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: '40px' }}>
                <ThreeDots
                    height="60"
                    width="60"
                    radius="9"
                    color="#3498db"
                    ariaLabel="loading"
                />
            </div>
        );
    }

    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div>

            <div className="notifications-container">
                <h1>Notifications</h1>
                {notifications.length === 0 ? (
                    <p>No notifications yet.</p>
                ) : (
                    <>
                        <button
                            className="toggle-form-button"
                            onClick={handleMarkAllAsRead}
                        >
                            Mark All as Read
                        </button>


                        <ul>
                            {notifications.map(notif => (
                                <li
                                    key={notif.id}
                                    style={{
                                        backgroundColor: notif.read ? '#f0f0f0' : '#fff8e1',
                                        border: '1px solid #ccc',
                                        borderRadius: '8px',
                                        marginBottom: '10px',
                                        padding: '10px',
                                        cursor: 'pointer'
                                    }}
                                    onClick={() => handleMarkAsRead(notif.id)}
                                >
                                    <strong>{notif.message}</strong>
                                    {notif.linkTo && (
                                        <div>
                                            <a href={notif.linkTo}>Go to screen</a>
                                        </div>
                                    )}
                                    <div style={{ fontSize: '0.8em', color: '#888' }}>
                                        {new Date(notif.timestamp).toLocaleDateString('en-GB')}{' '}
                                        {new Date(notif.timestamp).toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' })}
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </>
                )}

            </div>
        </div>
    );
}

export default NotificationsScreen;
