import React, { useState } from 'react';
import { addCompany } from '../services/companyService';
import '../styles/SignupScreen.css';

function SignupScreen() {
    const [company, setCompany] = useState({
        name: '',
        address: '',
        phone: '',
        email: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCompany({ ...company, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newCompany = await addCompany(company);
            alert('Company registered successfully!');
            setCompany({ name: '', address: '', phone: '', email: '' });
        } catch (error) {
            alert('Error registering company. Please try again.');
        }
    };

    return (
        <div>
            <h2>Register a New Company</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Company Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={company.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Address:</label>
                    <input
                        type="text"
                        name="address"
                        value={company.address}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        name="phone"
                        value={company.phone}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={company.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Register Company</button>
            </form>
        </div>
    );
}

export default SignupScreen;
