import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { addCompany, getCompanies, addEmployeeToCompany, addAdminToCompany } from '../services/companyService';
import { addUser } from '../services/userService';
import '../styles/SignupScreen.css';

function SignupScreen() {
    const navigate = useNavigate();
    const [company, setCompany] = useState({ name: '', address: '', phone: '', email: '' });
    const [admin, setAdmin] = useState({ name: '', email: '', phone: '', password: '' });
    const [existingCompanies, setExistingCompanies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetch existing companies on mount
    useEffect(() => {
        const fetchCompanies = async () => {
            try {
                const data = await getCompanies();
                setExistingCompanies(data ? Object.values(data) : []);
            } catch (err) {
                setError('Failed to fetch companies.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchCompanies();
    }, []);

    const handleChange = (setter) => (e) => {
        const { name, value } = e.target;
        setter((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // בדיקה אם שם החברה כבר קיים
            const isCompanyExists = existingCompanies.some((comp) => comp.name.toLowerCase() === company.name.toLowerCase());
            if (isCompanyExists) {
                alert('A company with this name already exists. Please choose a different name.');
                return;
            }

            // יצירת חברה חדשה
            const newCompany = await addCompany(company);

            // יצירת משתמש Admin
            const adminUser = { ...admin, role: 'admin', companyId: newCompany.id };
            const addedUser = await addUser(adminUser);

            // עדכון רשימת העובדים של החברה
            await addEmployeeToCompany(newCompany.id, addedUser.id);

            // עדכון החברה עם ה-adminId (המשתמש הראשון)
            await addAdminToCompany(newCompany.id, addedUser.id);

            alert('Company and admin registered successfully!');
            navigate('/');
        } catch (error) {
            console.error('Error registering company and admin:', error);
            alert('Error registering company and admin. Please try again.');
        }
    };

    return (
        <div className="signup-container">
            <h2>Register a New Company</h2>
            {isLoading ? (
                <p>Loading existing companies...</p>
            ) : error ? (
                <p className="error">{error}</p>
            ) : existingCompanies.length === 0 ? (
                <p>No existing companies found. You can register a new company below.</p>
            ) : (
                <div>
                    <h3>Existing Companies</h3>
                    <ul>
                        {existingCompanies.map((comp, index) => (
                            <li key={index}>{comp.name}</li>
                        ))}
                    </ul>
                </div>
            )}

            <form onSubmit={handleSubmit}>
                <h3>Company Details</h3>
                <div>
                    <label>Company Name:</label>
                    <input type="text" name="name" value={company.name} onChange={handleChange(setCompany)} required />
                </div>
                <div>
                    <label>Address:</label>
                    <input type="text" name="address" value={company.address} onChange={handleChange(setCompany)} required />
                </div>
                <div>
                    <label>Phone:</label>
                    <input type="text" name="phone" value={company.phone} onChange={handleChange(setCompany)} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={company.email} onChange={handleChange(setCompany)} required />
                </div>

                <h3>Admin Details</h3>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={admin.name} onChange={handleChange(setAdmin)} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={admin.email} onChange={handleChange(setAdmin)} required />
                </div>
                <div>
                    <label>Phone:</label>
                    <input type="text" name="phone" value={admin.phone} onChange={handleChange(setAdmin)} required />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" value={admin.password} onChange={handleChange(setAdmin)} required />
                </div>

                <button type="submit">Register Company and Admin</button>
                <button type="button" className="link-button" onClick={() => navigate('/')}>
                    Back to Login
                </button>
            </form>
        </div>
    );
}

export default SignupScreen;
