import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';

import Modal from 'react-modal';
import { toast, ToastContainer } from 'react-toastify';
import { getUsers, updateUser } from '../services/userService';
import '../styles/ProfileScreen.css';
import '../styles/Navbar.css';
import 'react-toastify/dist/ReactToastify.css';

import { fileToBase64 } from '../utils/fileUtils';

Modal.setAppElement('#root');

const ProfileScreen = () => {
    const [userData, setUserData] = useState(null);
    const [profileUser, setProfileUser] = useState(null);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        loadUserData();

    }, [navigate]);

    const fetchUser = async () => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        console.log("🔐 Logged user from localStorage (ProfileScreen):", loggedUser);

        if (!loggedUser) {
            navigate('/');
            return;
        }

        setUserData(loggedUser);

        try {
            const allUsers = await getUsers();
            const usersWithIds = Object.entries(allUsers).map(([id, user]) => ({ id, ...user }));
            const matchedUser = usersWithIds.find((u) => u.id === loggedUser.user.id);
            console.log("✅ Full user from DB:", matchedUser);
            setProfileUser(matchedUser);
        } catch (error) {
            console.error("❌ Error fetching user from DB:", error);
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            let updatedPhotoData = profileUser.photoData || '';
            if (profileUser.photoFile) {
                updatedPhotoData = await fileToBase64(profileUser.photoFile);
            }

            const updated = await updateUser(profileUser.id, {
                name: profileUser.name,
                email: profileUser.email,
                phone: profileUser.phone,
                role: profileUser.role,
                photoData: updatedPhotoData
            });

            setProfileUser(updated);
            toast.success('Profile updated!');
            setIsEditModalOpen(false);
        } catch (err) {
            toast.error('Update failed.');
            console.error(err);
        }
    };

    if (!userData || !profileUser) return <Loader />;

    const renderEditModal = () => (
        <Modal
            isOpen={isEditModalOpen}
            onRequestClose={() => setIsEditModalOpen(false)}
            className="modal"
            overlayClassName="modal-overlay"
        >
            <h3>Edit Your Profile</h3>
            <form className="addOrEdit-employee-form" onSubmit={handleUpdate}>
                <label>Name:</label>
                <input
                    type="text"
                    value={profileUser.name}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, name: e.target.value }))}
                    required
                />
                <label>Email:</label>
                <input
                    type="email"
                    value={profileUser.email}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, email: e.target.value }))}
                    required
                />
                <label>Phone:</label>
                <input
                    type="text"
                    value={profileUser.phone || ''}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, phone: e.target.value }))}
                    required
                />
                <label>Role:</label>
                <select
                    value={profileUser.role}
                    onChange={(e) => setProfileUser(prev => ({ ...prev, role: e.target.value }))}
                >
                    <option value="employee">Employee</option>
                    <option value="admin">Admin</option>
                </select>
                <label>Profile Photo:</label>
                <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setProfileUser(prev => ({ ...prev, photoFile: e.target.files[0] }))}
                />

                {profileUser.photoFile && (
                    <img
                        src={URL.createObjectURL(profileUser.photoFile)}
                        alt="Preview"
                        style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '8px', margin: '10px 0' }}
                    />
                )}
                {!profileUser.photoFile && profileUser.photoData && (
                    <img
                        src={profileUser.photoData}
                        alt="Current"
                        style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '8px', margin: '10px 0' }}
                    />
                )}
                <button type="submit">Save Changes</button>
                <button type="button" onClick={() => setIsEditModalOpen(false)} className="cancel-button">
                    Cancel
                </button>
            </form>
        </Modal>
    );

    return (
        <BackgroundWrapper >
            <>
                <Navbar />
                <div className="navbar-placeholder"></div>
                <div className="profile-container">
                    <div className="profile-info">
                        <h1>Welcome, {profileUser.name}</h1>
                        <p>Email: {profileUser.email}</p>
                        <p>Phone: {profileUser.phone}</p>
                        <p>Company: {userData.company?.name || 'N/A'}</p>
                        <p>Role: {profileUser.role}</p>
                        <img
                            src={profileUser.photoData || '/images/Profile.jpeg'}
                            alt="Profile"
                            className="employee-photo-horizontal"
                        />

                        <div className="profile-buttons-vertical">
                            <button onClick={() => setIsEditModalOpen(true)}>Edit Profile</button>
                            <button onClick={() => navigate('/home')}>Back to Home</button>
                        </div>

                    </div>
                </div>

                {renderEditModal()}
                <ToastContainer />
            </>
        </BackgroundWrapper >

    );
};

export default ProfileScreen;
