// React functions
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

// Third-party libraries
import { toast, ToastContainer } from 'react-toastify';

// Styles
import 'react-toastify/dist/ReactToastify.css';
import '../styles/ChatScreen.css';
import '../styles/Navbar.css';

// Services
import { getGroupMessages, sendGroupMessage, getPrivateMessages, sendPrivateMessage } from '../services/chatService';
import { getUsers } from '../services/userService';

// Internal components
import Navbar from './Navbar';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';

const ChatScreen = () => {
    const navigate = useNavigate();
    const { recipientId } = useParams();

    const [userData, setUserData] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    const messagesEndRef = useRef(null);
    const [companyUsers, setCompanyUsers] = useState([]);

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    // ◊ß◊®◊ô◊ê◊î ◊ú◊î◊ï◊ì◊¢◊ï◊™ ◊ë◊õ◊ú ◊§◊¢◊ù ◊©recipientId ◊ê◊ï userData ◊û◊©◊™◊†◊ô◊ù
    useEffect(() => {
        if (userData?.company?.id) {
            fetchMessages();
        }
    }, [userData, recipientId]);

    // ◊ß◊®◊ô◊ê◊î ◊†◊§◊®◊ì◊™ ◊ú◊®◊©◊ô◊û◊™ ◊î◊û◊©◊™◊û◊©◊ô◊ù ◊®◊ß ◊§◊¢◊ù ◊ê◊ó◊™ ◊õ◊©◊ô◊© userData
    useEffect(() => {
        if (userData?.company?.id) {
            fetchCompanyUsers();
        }
    }, [userData]);


    const fetchCompanyUsers = async () => {
        try {
            const companyId = userData?.company?.id;
            const currentUserId = userData?.user?.id;

            if (companyId) {
                const allUsers = await getUsers();
                const filtered = Object.entries(allUsers)
                    .map(([id, user]) => ({ id, ...user }))
                    .filter((user) =>
                        user.companyIds?.some((comp) => comp.companyId === companyId) &&
                        user.id !== currentUserId
                    );
                setCompanyUsers(filtered);
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchMessages = async () => {
        try {
            const companyId = userData.company.id;
            const userId = userData.user.id;

            let data;
            if (recipientId) {
                data = await getPrivateMessages(companyId, userId, recipientId);
            } else {
                data = await getGroupMessages(companyId);
            }

            const messagesArray = data
                ? Object.values(data).sort((a, b) => a.timestamp - b.timestamp)
                : [];

            setMessages(messagesArray);

            // üîî ◊¢◊ì◊õ◊ï◊ü ◊™◊ï◊ï◊ô◊™ ◊î◊ï◊ì◊¢◊î ◊©◊ú◊ê ◊†◊ß◊®◊ê◊î ◊ú◊§◊ô ◊°◊ï◊í ◊î◊¶'◊ê◊ò
            const updatedUnread = { ...unreadMessages };

            if (!recipientId) {
                // ◊¶◊≥◊ê◊ò ◊ß◊ë◊ï◊¶◊™◊ô ‚Äì ◊ê◊ù ◊ô◊© ◊î◊ï◊ì◊¢◊ï◊™ ◊©◊ú◊ê ◊†◊ß◊®◊ê◊ï ◊û◊î◊ê◊ó◊®◊ô◊ù
                const hasUnread = messagesArray.some(
                    (msg) =>
                        msg.senderId !== userId &&
                        !(msg.readBy?.includes(userId))
                );
                updatedUnread['group'] = hasUnread;
            } else {
                // ◊¶◊≥◊ê◊ò ◊§◊®◊ò◊ô ‚Äì ◊ê◊ù ◊ô◊© ◊î◊ï◊ì◊¢◊ï◊™ ◊û◊î◊©◊ï◊ú◊ó ◊©◊ú◊ê ◊†◊ß◊®◊ê◊ï
                const hasUnread = messagesArray.some(
                    (msg) =>
                        msg.senderId === recipientId &&
                        !(msg.readBy?.includes(userId))
                );
                updatedUnread[recipientId] = hasUnread;
            }

            setUnreadMessages(updatedUnread);
        } catch (error) {
            console.error('Error fetching messages:', error);
            toast.error('Failed to load messages.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleSendMessage = async () => {
        if (!newMessage.trim()) return;

        const messageData = {
            senderId: userData.user.id,
            senderName: userData.user.name || 'Unknown',
            content: newMessage.trim(),
            timestamp: Date.now(),
        };

        try {
            const companyId = userData.company.id;
            const userId = userData.user.id;

            if (recipientId) {
                await sendPrivateMessage(companyId, userId, recipientId, messageData);
            } else {
                await sendGroupMessage(companyId, messageData);
            }

            setMessages((prev) => [...prev, messageData]);
            setNewMessage('');
            scrollToBottom();
        } catch (error) {
            toast.error('Failed to send message.');
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(scrollToBottom, [messages]);

    const isSameDate = (timestamp1, timestamp2) => {
        const date1 = new Date(timestamp1);
        const date2 = new Date(timestamp2);
        return (
            date1.getFullYear() === date2.getFullYear() &&
            date1.getMonth() === date2.getMonth() &&
            date1.getDate() === date2.getDate()
        );
    };

    const getUserNameById = (id) => {
        const user = companyUsers.find((u) => u.id === id);
        return user ? user.name : '◊û◊©◊™◊û◊©';
    };

    if (!userData) return <Loader />;

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div>

            <div className="chat-layout">
                {/* Sidebar with user list */}
                <div className="chat-sidebar">
                    <h3>üí¨ ◊©◊ô◊ó◊ï◊™</h3>
                    <ul>
                        <li
                            className={`chat-link ${!recipientId ? 'active-chat' : ''}`}
                            onClick={() => navigate('/chat')}
                        >
                            {unreadMessages['group'] && <span className="unread-dot"></span>}
                            üü¢ ◊¶'◊ê◊ò ◊ß◊ë◊ï◊¶◊™◊ô
                        </li>

                        {companyUsers.map((user) => (
                            <li
                                key={user.id}
                                className={`chat-link ${recipientId === user.id ? 'active-chat' : ''}`}
                                onClick={() => navigate(`/private-chat/${user.id}`)}
                            >
                                {unreadMessages[user.id] && <span className="unread-dot"></span>}
                                {user.name}
                            </li>
                        ))}
                    </ul>


                </div>

                {/* Main chat area */}
                <div className="chat-main">
                    <h1>{recipientId ? `◊©◊ô◊ó◊î ◊¢◊ù ${getUserNameById(recipientId)}` : '◊¶\'◊ê◊ò ◊ß◊ë◊ï◊¶◊™◊ô'}</h1>

                    {isLoading ? (
                        <Loader />
                    ) : (
                        <>
                            <div className="chat-messages">
                                {messages.map((msg, index) => {
                                    const currentDate = new Date(msg.timestamp);
                                    const prevDate = index > 0 ? new Date(messages[index - 1].timestamp) : null;
                                    const showDate = index === 0 || !isSameDate(currentDate, prevDate);

                                    return (
                                        <React.Fragment key={index}>
                                            {showDate && (
                                                <div className="date-header-wrapper">
                                                    <div className="date-header">
                                                        {currentDate.toLocaleDateString('he-IL', {
                                                            weekday: 'short',
                                                            day: '2-digit',
                                                            month: '2-digit',
                                                            year: 'numeric',
                                                        })}
                                                    </div>
                                                </div>
                                            )}
                                            <div className={`chat-message-wrapper ${msg.senderId === userData.user.id ? 'align-right' : 'align-left'}`}>
                                                <div className={`chat-message ${msg.senderId === userData.user.id ? 'my-message' : 'other-message'}`}>
                                                    <div className="chat-sender">{msg.senderName}</div>
                                                    <div className="chat-content">{msg.content}</div>
                                                    <div className="chat-time">
                                                        {new Date(msg.timestamp).toLocaleTimeString('he-IL', {
                                                            hour: '2-digit',
                                                            minute: '2-digit',
                                                        })}
                                                    </div>
                                                </div>
                                            </div>
                                        </React.Fragment>
                                    );
                                })}
                                <div ref={messagesEndRef} />
                            </div>

                            <div className="chat-input">
                                <input
                                    type="text"
                                    placeholder="Type a message..."
                                    value={newMessage}
                                    onChange={(e) => setNewMessage(e.target.value)}
                                    onKeyDown={handleKeyPress}
                                />
                                <button onClick={handleSendMessage}>Send</button>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </BackgroundWrapper>
    );
};

export default ChatScreen;
