import axios from 'axios';

const API_URL = 'http://localhost:5000/api/notifications';

// שליפת ההודעות למשתמש
export const getUserNotifications = async (companyId, userId) => {
    const res = await fetch(`${API_BASE}/${companyId}/${userId}`);
    if (!res.ok) throw new Error('Failed to fetch notifications');
    return await res.json();
};

// שליחת התראה למשתמש
export const sendNotification = async (companyId, userId, message, linkTo = '') => {
    try {
        const response = await axios.post(`${API_URL}/${companyId}/${userId}`, {
            message,
            linkTo
        });
        return response.data;
    } catch (error) {
        console.error('Error sending notification:', error.message);
        throw error;
    }
};

// סימון הודעה כנקראה
export const markAsRead = async (companyId, userId, notificationId) => {
    const res = await fetch(`${API_BASE}/${companyId}/${userId}/${notificationId}/read`, {
        method: 'POST'
    });
    if (!res.ok) throw new Error('Failed to mark notification as read');
};
