import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
    const navigate = useNavigate();

    // שליפת פרטי המשתמש מה-localStorage
    const loggedUser = JSON.parse(localStorage.getItem('user'));
    const isAdmin = loggedUser?.user?.role === 'admin'; // בדיקה אם המשתמש הוא admin

    return (
        <nav className="navbar">
            <ul className="navbar-list">
                <li className="navbar-item">
                    <Link to="/home" className="navbar-link">Home</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/profile" className="navbar-link">Profile</Link>
                </li>
                {isAdmin && ( // הצגת כפתור Employees רק אם המשתמש הוא admin
                    <li className="navbar-item">
                        <Link to="/employees" className="navbar-link">Employees</Link>
                    </li>
                )}
                <li className="navbar-item">
                    <Link to="/availability" className="navbar-link">Availability</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/schedule" className="navbar-link">Schedule</Link>
                </li>
                <li className="navbar-item">
                    <button
                        onClick={() => navigate('/')}
                        className="navbar-link logout-button"
                    >
                        Logout
                    </button>
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
