import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Loader from './Loader';

import Modal from 'react-modal';
import { toast, ToastContainer } from 'react-toastify';
import { getUsers, updateUser } from '../services/userService';
import '../styles/ProfileScreen.css';
import '../styles/Navbar.css';
import 'react-toastify/dist/ReactToastify.css';

Modal.setAppElement('#root');

const ProfileScreen = () => {
    const [userData, setUserData] = useState(null);
    const [fullUserFromDB, setFullUserFromDB] = useState(null);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUser = async () => {
            const loggedUser = JSON.parse(localStorage.getItem('user'));
            console.log("ðŸ” Logged user from localStorage (ProfileScreen):", loggedUser);

            if (!loggedUser) {
                navigate('/');
                return;
            }

            setUserData(loggedUser);

            try {
                const allUsers = await getUsers();
                const usersWithIds = Object.entries(allUsers).map(([id, user]) => ({ id, ...user }));
                const matchedUser = usersWithIds.find((u) => u.id === loggedUser.user.id);
                console.log("âœ… Full user from DB:", matchedUser);
                setFullUserFromDB(matchedUser);
            } catch (error) {
                console.error("âŒ Error fetching user from DB:", error);
            }
        };

        fetchUser();
    }, [navigate]);

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            let updatedPhotoData = fullUserFromDB.photoData || '';
            if (fullUserFromDB.photoFile) {
                const reader = new FileReader();
                updatedPhotoData = await new Promise((resolve, reject) => {
                    reader.onloadend = () => resolve(reader.result);
                    reader.onerror = reject;
                    reader.readAsDataURL(fullUserFromDB.photoFile);
                });
            }

            const updated = await updateUser(fullUserFromDB.id, {
                name: fullUserFromDB.name,
                email: fullUserFromDB.email,
                phone: fullUserFromDB.phone,
                role: fullUserFromDB.role,
                photoData: updatedPhotoData
            });

            setFullUserFromDB(updated);
            toast.success('Profile updated!');
            setIsEditModalOpen(false);
        } catch (err) {
            toast.error('Update failed.');
            console.error(err);
        }
    };

    if (!userData || !fullUserFromDB) return <Loader />;

    return (
        <>
            <Navbar />
            <div className="navbar-placeholder"></div>
            <div className="profile-container">
                <div className="profile-info">
                    <h1>Welcome, {fullUserFromDB.name}</h1>
                    <p>Email: {fullUserFromDB.email}</p>
                    <p>Phone: {fullUserFromDB.phone}</p>
                    <p>Company: {userData.company?.name || 'N/A'}</p>
                    <p>Role: {fullUserFromDB.role}</p>
                    <img
                        src={fullUserFromDB.photoData || '/images/Profile.jpeg'}
                        alt="Profile"
                        className="employee-photo-horizontal"
                    />

                    <button onClick={() => setIsEditModalOpen(true)}>Edit Profile</button>
                    <button onClick={() => navigate('/home')}>Back to Home</button>
                </div>
            </div>

            <Modal
                isOpen={isEditModalOpen}
                onRequestClose={() => setIsEditModalOpen(false)}
                className="modal"
                overlayClassName="modal-overlay"
            >
                <h3>Edit Your Profile</h3>
                <form className="addOrEdit-employee-form" onSubmit={handleUpdate}>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={fullUserFromDB.name}
                        onChange={(e) => setFullUserFromDB(prev => ({ ...prev, name: e.target.value }))}
                        required
                    />
                    <label>Email:</label>
                    <input
                        type="email"
                        value={fullUserFromDB.email}
                        onChange={(e) => setFullUserFromDB(prev => ({ ...prev, email: e.target.value }))}
                        required
                    />
                    <label>Phone:</label>
                    <input
                        type="text"
                        value={fullUserFromDB.phone || ''}
                        onChange={(e) => setFullUserFromDB(prev => ({ ...prev, phone: e.target.value }))}
                        required
                    />
                    <label>Role:</label>
                    <select
                        value={fullUserFromDB.role}
                        onChange={(e) => setFullUserFromDB(prev => ({ ...prev, role: e.target.value }))}
                    >
                        <option value="employee">Employee</option>
                        <option value="admin">Admin</option>
                    </select>
                    <label>Profile Photo:</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => setFullUserFromDB(prev => ({ ...prev, photoFile: e.target.files[0] }))}
                    />

                    {fullUserFromDB.photoFile && (
                        <img
                            src={URL.createObjectURL(fullUserFromDB.photoFile)}
                            alt="Preview"
                            style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '8px', margin: '10px 0' }}
                        />
                    )}
                    {!fullUserFromDB.photoFile && fullUserFromDB.photoData && (
                        <img
                            src={fullUserFromDB.photoData}
                            alt="Current"
                            style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '8px', margin: '10px 0' }}
                        />
                    )}
                    <button type="submit">Save Changes</button>
                    <button type="button" onClick={() => setIsEditModalOpen(false)} className="cancel-button">
                        Cancel
                    </button>
                </form>
            </Modal>

            <ToastContainer />
        </>
    );
};

export default ProfileScreen;
