// src/components/NotificationsScreen.js
import React, { useEffect, useState } from 'react';
import { getUserNotifications, markAsRead } from '../services/notificationService';
import '../styles/NotificationsScreen.css';
import { fetchNotifications, sendNotification } from '../services/notificationService';

function NotificationsScreen() {
    const [notifications, setNotifications] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));
    const companyId = user?.company?.id;
    const userId = user?.user?.id;

    useEffect(() => {
        const fetchNotifications = async () => {
            try {
                const data = await getUserNotifications(companyId, userId);
                const formatted = Object.entries(data || {}).map(([id, value]) => ({
                    id,
                    ...value
                }));
                setNotifications(formatted.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp)));
            } catch (err) {
                setError('Failed to load notifications.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        if (companyId && userId) {
            fetchNotifications();
        }
    }, [companyId, userId]);

    const handleMarkAsRead = async (notificationId) => {
        await markAsRead(companyId, userId, notificationId);
        setNotifications((prev) =>
            prev.map((n) => (n.id === notificationId ? { ...n, read: true } : n))
        );
    };

    return (
        <div className="notifications-screen">
            <h1>Notifications</h1>
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p className="error">{error}</p>
            ) : notifications.length === 0 ? (
                <p>No notifications.</p>
            ) : (
                <ul className="notification-list">
                    {notifications.map((n) => (
                        <li
                            key={n.id}
                            className={`notification-item ${n.read ? 'read' : 'unread'}`}
                            onClick={() => {
                                if (!n.read) handleMarkAsRead(n.id);
                                if (n.linkTo) window.location.href = n.linkTo;
                            }}
                        >
                            <div className="message">{n.message}</div>
                            <div className="time">{new Date(n.timestamp).toLocaleString()}</div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default NotificationsScreen;
