import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { saveAvailability, getAvailability } from '../services/availabilityService';
import { getUsers } from '../services/userService';
import '../styles/AvailabilityScreen.css';

const AvailabilityScreen = () => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const shifts = ['Morning', 'Noon', 'Evening', 'Night'];

    const [employees, setEmployees] = useState([]);
    const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);
    const [availability, setAvailability] = useState(null);
    const [notes, setNotes] = useState('');
    const [currentWeek, setCurrentWeek] = useState(1);
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);

            if (loggedUser.user.role === 'admin') {
                const fetchEmployees = async () => {
                    try {
                        const usersData = await getUsers();
                        const companyId = loggedUser.company.id;

                        if (companyId) {
                            const filteredEmployees = Object.entries(usersData)
                                .map(([id, user]) => ({ id, ...user }))
                                .filter((user) =>
                                    user.companyIds?.some((company) => company.companyId === companyId)
                                );

                            setEmployees(filteredEmployees);
                        }
                    } catch (error) {
                        console.error('Error fetching employees:', error);
                    }
                };

                fetchEmployees();
            } else {
                // Non-admin users can only fill their own availability
                setSelectedEmployeeId(loggedUser.id);
            }
        }
    }, [navigate]);

    useEffect(() => {
        const fetchAvailability = async () => {
            if (!selectedEmployeeId || !userData) return;

            const companyId = userData.company.id;
            const weekKey = calculateWeekKey(currentWeek);

            try {
                const fetchedAvailability = await getAvailability(companyId, weekKey, selectedEmployeeId);
                setAvailability(initializeAvailability(fetchedAvailability || {}));
                setNotes(fetchedAvailability?.notes || '');
            } catch (error) {
                console.error('Error fetching availability:', error);
                alert('Failed to fetch availability. Please try again.');
            }
        };

        fetchAvailability();
    }, [currentWeek, selectedEmployeeId, userData]);

    const calculateWeekKey = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));
        const formatDate = (date) => `${date.getFullYear()}_${String(date.getMonth() + 1).padStart(2, '0')}_${String(date.getDate()).padStart(2, '0')}`;
        return `week_${formatDate(firstDayOfWeek)}`;
    };

    const calculateWeekRange = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));
        const lastDayOfWeek = new Date(firstDayOfWeek);
        lastDayOfWeek.setDate(firstDayOfWeek.getDate() + 6);

        const formatDate = (date) => {
            const day = String(date.getDate()).padStart(2, '0');
            const month = String(date.getMonth() + 1).padStart(2, '0');
            const year = date.getFullYear();
            return `${day}/${month}/${year}`;
        };

        return `${formatDate(firstDayOfWeek)} - ${formatDate(lastDayOfWeek)}`;
    };

    const initializeAvailability = (existingAvailability) => {
        return shifts.reduce((acc, shift) => {
            acc[shift] = daysOfWeek.reduce((dayAcc, day) => {
                dayAcc[day] = existingAvailability[shift]?.[day] || false;
                return dayAcc;
            }, {});
            return acc;
        }, {});
    };

    const handleAvailabilityChange = (shift, day) => {
        setAvailability((prev) => ({
            ...prev,
            [shift]: {
                ...prev[shift],
                [day]: !prev[shift][day],
            },
        }));
    };

    const handleWeekChange = (direction) => {
        setCurrentWeek((prevWeek) => prevWeek + direction);
    };

    const handleSubmit = async () => {
        if (!selectedEmployeeId) {
            alert('Please select an employee to save availability.');
            return;
        }

        const companyId = userData.company.id;
        const weekKey = calculateWeekKey(currentWeek);

        try {
            const dataToSave = {
                ...availability,
                notes,
            };

            await saveAvailability(companyId, weekKey, selectedEmployeeId, dataToSave);
            alert('Availability and notes saved successfully!');
        } catch (error) {
            console.error('Error saving availability or notes:', error);
            alert('Failed to save availability or notes. Please try again.');
        }
    };

    if (!userData) {
        return <p>Loading...</p>;
    }

    return (
        <div className="availability-container">
            <Navbar />
            <h1>Weekly Availability</h1>
            {userData.user.role === 'admin' && (
                <div className="employee-selector">
                    <label htmlFor="employeeDropdown">Select Employee:</label>
                    <select
                        id="employeeDropdown"
                        onChange={(e) => setSelectedEmployeeId(e.target.value)}
                        value={selectedEmployeeId || ''}
                    >
                        <option value="">Select an employee</option>
                        {employees.map((employee) => (
                            <option key={employee.id} value={employee.id}>
                                {employee.name}
                            </option>
                        ))}
                    </select>
                </div>
            )}
            {selectedEmployeeId && (
                <>
                    <div className="week-navigation">
                        <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                        <h2>{calculateWeekRange(currentWeek)}</h2>
                        <button onClick={() => handleWeekChange(1)}>Next Week</button>
                    </div>
                    <table className="availability-table">
                        <thead>
                            <tr>
                                <th>Shift</th>
                                {daysOfWeek.map((day) => (
                                    <th key={day}>{day}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {shifts.map((shift) => (
                                <tr key={shift}>
                                    <td>{shift}</td>
                                    {daysOfWeek.map((day) => (
                                        <td key={day}>
                                            <input
                                                type="checkbox"
                                                checked={availability?.[shift]?.[day] || false}
                                                onChange={() => handleAvailabilityChange(shift, day)}
                                            />
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <textarea
                        className="notes-textarea"
                        placeholder="Write weekly notes here..."
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                    ></textarea>
                    <button className="submit-button" onClick={handleSubmit}>
                        Submit
                    </button>
                </>
            )}
        </div>
    );
};

export default AvailabilityScreen;
