import React, { useState, useEffect } from 'react';
import { getAllAvailabilities } from '../services/availabilityService';
import { getUsers } from '../services/userService';
import { calculateWeekKey, calculateWeekRange } from '../utils/utils';
import { updateAvailabilityStatus } from '../services/availabilityService';
import Navbar from '../components/Navbar';
import '../styles/ScheduleScreen.css';

function ScheduleScreen() {
    const [availability, setAvailability] = useState({});
    const [users, setUsers] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [weekOffset, setWeekOffset] = useState(0); // Offset לשבוע הנוכחי
    const [currentCompany, setCurrentCompany] = useState(null);
    const [weekDates, setWeekDates] = useState([]); // תאריכים לכל הימים בשבוע

    useEffect(() => {
        const fetchAvailabilityAndUsers = async () => {
            try {
                const loggedUser = JSON.parse(localStorage.getItem('user'));
                const companyId = loggedUser?.company?.id;

                if (!companyId) {
                    setError('No company information found.');
                    return;
                }

                setCurrentCompany(companyId);

                const weekKey = calculateWeekKey(weekOffset);

                // שליפת זמינות ומשתמשים
                const [availabilityData, usersData] = await Promise.all([
                    getAllAvailabilities(companyId, weekKey),
                    getUsers()
                ]);

                setAvailability(availabilityData || {});
                setUsers(usersData || {});

                // חישוב תאריכים לשבוע הנוכחי
                const startOfWeek = new Date();
                startOfWeek.setDate(startOfWeek.getDate() + weekOffset * 7 - startOfWeek.getDay());
                const dates = Array.from({ length: 7 }, (_, i) => {
                    const date = new Date(startOfWeek);
                    date.setDate(startOfWeek.getDate() + i);
                    return date.toLocaleDateString('en-GB'); // פורמט DD/MM/YYYY
                });
                setWeekDates(dates);
            } catch (err) {
                setError('Failed to fetch data.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchAvailabilityAndUsers();
    }, [weekOffset]);

    const handleWeekChange = (direction) => {
        setWeekOffset((prevOffset) => prevOffset + direction);
    };

    const getStatusClass = (status) => {
        switch (status) {
            case 'selected':
                return 'status-selected';
            case 'disabled':
                return 'status-disabled';
            case 'default':
            default:
                return 'status-default';
        }
    };

    const handleStatusUpdate = async (userId, shift, day, currentStatus) => {
        if (currentStatus === 'disabled') return; // Disable click for disabled status

        try {
            const weekKey = calculateWeekKey(weekOffset);
            const newStatus = currentStatus === 'default' ? 'selected' : 'default';

            const updatedAvailability = { ...availability };

            // Update the current shift's status to 'selected' or 'default'
            updatedAvailability[userId][shift][day].status = newStatus;
            await updateAvailabilityStatus(currentCompany, weekKey, userId, shift, day, newStatus);

            if (newStatus === 'selected') {
                // Disable other shifts on the same day
                for (const otherShift of ['Morning', 'Noon', 'Evening', 'Night']) {
                    if (otherShift !== shift) {
                        updatedAvailability[userId][otherShift][day].status = 'disabled';
                        await updateAvailabilityStatus(currentCompany, weekKey, userId, otherShift, day, 'disabled');
                    }
                }

                // Handle unique rules for specific shifts
                if (shift === 'Morning' || shift === 'Noon') {
                    let previousDay = calculatePreviousDay(day);
                    let targetWeekKey = weekKey; // ברירת מחדל: השבוע הנוכחי

                    // אם היום הוא יום ראשון, יש להשפיע על יום שבת של השבוע הקודם
                    if (day === 'Sunday') {
                        targetWeekKey = calculateWeekKey(weekOffset - 1); // מפתח השבוע הקודם
                        previousDay = 'Saturday'; // היום הקודם הוא יום שבת

                        // בדוק אם השבוע הקודם קיים ב-UI, ואם לא, צור אותו
                        if (!updatedAvailability[userId]?.['Night']?.[previousDay]) {
                            if (!updatedAvailability[userId]) updatedAvailability[userId] = {};
                            if (!updatedAvailability[userId]['Night']) updatedAvailability[userId]['Night'] = {};
                            updatedAvailability[userId]['Night'][previousDay] = { status: 'default' }; // הגדר מבנה נתונים חדש
                        }
                    }

                    // עדכון הסטטוס במשמרת הלילה של היום הקודם
                    if (
                        updatedAvailability[userId]?.['Night']?.[previousDay]?.status === 'default' ||
                        updatedAvailability[userId]?.['Night']?.[previousDay]?.status === 'selected'
                    ) {
                        updatedAvailability[userId]['Night'][previousDay].status = 'disabled';
                        await updateAvailabilityStatus(currentCompany, targetWeekKey, userId, 'Night', previousDay, 'disabled');
                    }
                }
 else if (shift === 'Night') {
                    const nextDay = calculateNextDay(day);

                    // Handle shifts in the next week if day is Saturday
                    if (day === 'Saturday') {
                        const nextWeekKey = calculateWeekKey(weekOffset + 1); // Next week's key
                        if (updatedAvailability[userId]?.['Morning']?.['Sunday']) {
                            await updateAvailabilityStatus(currentCompany, nextWeekKey, userId, 'Morning', 'Sunday', 'disabled');
                        }
                        if (updatedAvailability[userId]?.['Noon']?.['Sunday']) {
                            await updateAvailabilityStatus(currentCompany, nextWeekKey, userId, 'Noon', 'Sunday', 'disabled');
                        }
                    } else {
                        if (updatedAvailability[userId]?.['Morning']?.[nextDay]) {
                            updatedAvailability[userId]['Morning'][nextDay].status = 'disabled';
                            await updateAvailabilityStatus(currentCompany, weekKey, userId, 'Morning', nextDay, 'disabled');
                        }
                        if (updatedAvailability[userId]?.['Noon']?.[nextDay]) {
                            updatedAvailability[userId]['Noon'][nextDay].status = 'disabled';
                            await updateAvailabilityStatus(currentCompany, weekKey, userId, 'Noon', nextDay, 'disabled');
                        }
                    }
                }
            } else {
                // Undo selection and revert unique rules
                for (const otherShift of ['Morning', 'Noon', 'Evening', 'Night']) {
                    if (otherShift !== shift && updatedAvailability[userId][otherShift][day].status === 'disabled') {
                        if (otherShift === 'Morning' || otherShift === 'Noon') {
                            // Check if the previous night's shift is not selected
                            const previousDay = calculatePreviousDay(day);
                            if (
                                updatedAvailability[userId]?.['Night']?.[previousDay]?.status !== 'selected'
                            ) {
                                updatedAvailability[userId][otherShift][day].status = 'default';
                                await updateAvailabilityStatus(currentCompany, weekKey, userId, otherShift, day, 'default');
                            }
                        } else if (otherShift === 'Night') {
                            // Check if none of the next day's morning or noon shifts are selected
                            const nextDay = calculateNextDay(day);
                            if (
                                !(
                                    updatedAvailability[userId]?.['Morning']?.[nextDay]?.status === 'selected' ||
                                    updatedAvailability[userId]?.['Noon']?.[nextDay]?.status === 'selected'
                                )
                            ) {
                                updatedAvailability[userId][otherShift][day].status = 'default';
                                await updateAvailabilityStatus(currentCompany, weekKey, userId, otherShift, day, 'default');
                            }
                        } else {
                            updatedAvailability[userId][otherShift][day].status = 'default';
                            await updateAvailabilityStatus(currentCompany, weekKey, userId, otherShift, day, 'default');
                        }
                    }
                }

                // Revert unique rules for specific shifts
                if (shift === 'Morning' || shift === 'Noon') {
                    const previousDay = calculatePreviousDay(day);

                    // New logic: Check if other shifts on the previous day are selected
                    const hasOtherSelectedShifts = ['Morning', 'Noon', 'Evening'].some(
                        (s) => updatedAvailability[userId]?.[s]?.[previousDay]?.status === 'selected'
                    );

                    if (
                        updatedAvailability[userId]?.['Night']?.[previousDay]?.status === 'disabled' &&
                        !hasOtherSelectedShifts
                    ) {
                        updatedAvailability[userId]['Night'][previousDay].status = 'default';
                        await updateAvailabilityStatus(currentCompany, weekKey, userId, 'Night', previousDay, 'default');
                    }
                } else if (shift === 'Night') {
                    const nextDay = calculateNextDay(day);

                    // Revert shifts in the next week if day is Saturday
                    if (day === 'Saturday') {
                        const nextWeekKey = calculateWeekKey(weekOffset + 1); // Next week's key

                        const hasSelectedShiftsOnSunday = ['Morning', 'Noon', 'Evening', 'Night'].some(
                            (s) => updatedAvailability[userId]?.[s]?.['Sunday']?.status === 'selected'
                        );

                        if (!hasSelectedShiftsOnSunday && updatedAvailability[userId]?.['Morning']?.['Sunday']) {
                            await updateAvailabilityStatus(currentCompany, nextWeekKey, userId, 'Morning', 'Sunday', 'default');
                        }
                        if (!hasSelectedShiftsOnSunday && updatedAvailability[userId]?.['Noon']?.['Sunday']) {
                            await updateAvailabilityStatus(currentCompany, nextWeekKey, userId, 'Noon', 'Sunday', 'default');
                        }
                    } else {
                        const hasSelectedShiftsOnNextDay = ['Morning', 'Noon', 'Evening', 'Night'].some(
                            (s) => updatedAvailability[userId]?.[s]?.[nextDay]?.status === 'selected'
                        );

                        if (!hasSelectedShiftsOnNextDay && updatedAvailability[userId]?.['Morning']?.[nextDay]?.status === 'disabled') {
                            updatedAvailability[userId]['Morning'][nextDay].status = 'default';
                            await updateAvailabilityStatus(currentCompany, weekKey, userId, 'Morning', nextDay, 'default');
                        }
                        if (!hasSelectedShiftsOnNextDay && updatedAvailability[userId]?.['Noon']?.[nextDay]?.status === 'disabled') {
                            updatedAvailability[userId]['Noon'][nextDay].status = 'default';
                            await updateAvailabilityStatus(currentCompany, weekKey, userId, 'Noon', nextDay, 'default');
                        }
                    }
                }
            }

            // Update state with the modified availability
            setAvailability(updatedAvailability);
        } catch (error) {
            console.error('Failed to update status:', error);
        }
    };



    // Helper functions to calculate previous and next days
    const calculatePreviousDay = (currentDay) => {
        const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const currentIndex = daysOfWeek.indexOf(currentDay);
        return currentIndex > 0 ? daysOfWeek[currentIndex - 1] : daysOfWeek[6];
    };

    const calculateNextDay = (currentDay) => {
        const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const currentIndex = daysOfWeek.indexOf(currentDay);
        return currentIndex < 6 ? daysOfWeek[currentIndex + 1] : daysOfWeek[0];
    };

    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* רווח לניווט */}

            <div className="schedule-container">
                <h2>Weekly Schedule</h2>
                <div className="week-navigation">
                    <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                    <span className="week-range">{calculateWeekRange(weekOffset)}</span>
                    <button onClick={() => handleWeekChange(1)}>Next Week</button>
                </div>

                {isLoading ? (
                    <p>Loading schedule...</p>
                ) : error ? (
                    <p className="error">{error}</p>
                ) : (
                    <table className="schedule-table">
                        <thead>
                            <tr>
                                <th>Shift</th>
                                {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map(
                                    (day, index) => (
                                        <th key={day}>
                                            {day} <br />
                                            <span className="date">{weekDates[index]}</span>
                                        </th>
                                    )
                                )}
                            </tr>
                        </thead>
                        <tbody>
                            {['Morning', 'Noon', 'Evening', 'Night'].map((shift) => (
                                <tr key={shift}>
                                    <td>{shift}</td>
                                    {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map(
                                        (day) => (
                                            <td key={day}>
                                                {Object.entries(availability).map(([userId, userData]) => {
                                                    const availabilityInfo = userData?.[shift]?.[day];
                                                    if (availabilityInfo?.isAvailable) {
                                                        const statusClass = getStatusClass(availabilityInfo.status);
                                                        return (
                                                            <div
                                                                key={userId}
                                                                className={`availability-entry ${statusClass}`}
                                                                onClick={() =>
                                                                    handleStatusUpdate(
                                                                        userId,
                                                                        shift,
                                                                        day,
                                                                        availabilityInfo.status
                                                                    )
                                                                }
                                                                style={{
                                                                    backgroundColor:
                                                                        availabilityInfo.status === 'selected'
                                                                            ? '#2ecc71'
                                                                            : availabilityInfo.status === 'disabled'
                                                                                ? '#e74c3c'
                                                                                : '#ecf0f1',
                                                                    color: availabilityInfo.status === 'disabled' ? 'white' : '#2c3e50',
                                                                    pointerEvents: availabilityInfo.status === 'disabled' ? 'none' : 'auto',
                                                                    cursor: availabilityInfo.status === 'disabled' ? 'not-allowed' : 'pointer',
                                                                }}
                                                            >
                                                                {users[userId]?.name || 'Unknown'}
                                                            </div>
                                                        );
                                                    }
                                                    return null;
                                                })}
                                            </td>
                                        )
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default ScheduleScreen;
