import React, { useEffect, useState } from 'react';
import { getMonthlyStats } from '../services/statsService';
import '../styles/StatsScreen.css';

const StatsScreen = () => {
    const [stats, setStats] = useState([]);
    const [loading, setLoading] = useState(true);
    const [month, setMonth] = useState(new Date().getMonth() + 1);
    const [year, setYear] = useState(new Date().getFullYear());

    useEffect(() => {
        const fetchStats = async () => {
            if (!currentUser || !currentCompany) return;

            setLoading(true);
            try {
                const data = await getMonthlyStats(currentCompany, year, month, currentUser.id);
                setStats(data || []);
            } catch (error) {
                console.error('Error fetching stats:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchStats();
    }, [currentCompany, currentUser, month, year]);

    return (
        <div className="stats-screen">
            <h2>Monthly Shift Statistics</h2>

            <div className="stats-controls">
                <label>
                    Month:
                    <input
                        type="number"
                        min="1"
                        max="12"
                        value={month}
                        onChange={(e) => setMonth(parseInt(e.target.value))}
                    />
                </label>
                <label>
                    Year:
                    <input
                        type="number"
                        min="2020"
                        max="2099"
                        value={year}
                        onChange={(e) => setYear(parseInt(e.target.value))}
                    />
                </label>
            </div>

            {loading ? (
                <p>Loading...</p>
            ) : stats.length === 0 ? (
                <p>No stats found for this month.</p>
            ) : (
                <table className="stats-table">
                    <thead>
                        <tr>
                            <th>Week</th>
                            <th>Night Shifts</th>
                            <th>Shabbat Shifts</th>
                            <th>Regular Shifts</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stats.map((stat, idx) => (
                            <tr key={idx}>
                                <td>{stat.weekKey}</td>
                                <td>{stat.nightShifts || 0}</td>
                                <td>{stat.shabbatShifts || 0}</td>
                                <td>{stat.regularShifts || 0}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default StatsScreen;
