import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import {
    getAvailabilityByUserId,
    saveAvailability,
    updateAvailability,
    calculateWeekDates,
} from '../services/availabilityService';
import '../styles/AvailabilityScreen.css';

const AvailabilityScreen = () => {
    const [availability, setAvailability] = useState({});
    const [weekDates, setWeekDates] = useState([]);
    const [availabilityExists, setAvailabilityExists] = useState(false); // האם זמינות קיימת
    const navigate = useNavigate();
    const userId = JSON.parse(localStorage.getItem('user'))?.user?.id; // קבלת ה-userId מה-localStorage

    useEffect(() => {
        if (!userId) {
            navigate('/'); // אם המשתמש לא מחובר, ננווט למסך ההתחברות
            return;
        }

        // חישוב תאריכי השבוע
        setWeekDates(calculateWeekDates());

        // טעינת זמינות מהשרת
        const loadAvailability = async () => {
            try {
                const data = await getAvailabilityByUserId(userId);
                if (data) {
                    setAvailability(data);
                    setAvailabilityExists(true);
                }
            } catch (error) {
                console.error('Error fetching availability:', error);
            }
        };

        loadAvailability();
    }, [navigate, userId]);

    const handleAvailabilityChange = (date, shift, isAvailable) => {
        setAvailability((prev) => ({
            ...prev,
            [date]: {
                ...prev[date],
                [shift]: isAvailable,
            },
        }));
    };

    const handleSave = async () => {
        try {
            const response = availabilityExists
                ? await updateAvailability(userId, { weekDates, availability })
                : await saveAvailability(userId, { weekDates, availability });

            alert(availabilityExists ? 'Availability updated successfully!' : 'Availability saved successfully!');
            console.log('Saved/Updated Availability:', response);
        } catch (error) {
            console.error('Error saving/updating availability:', error);
            alert('Failed to save/update availability. Please try again.');
        }
    };

    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    return (
        <>
            <Navbar />
            <div className="availability-container">
                <h2>Set Your Availability</h2>
                <p>Week: {weekDates[0]} to {weekDates[6]}</p>
                <table className="availability-table">
                    <thead>
                        <tr>
                            <th>Shifts</th>
                            {weekDates.map((date, index) => (
                                <th key={date}>
                                    {daysOfWeek[index]}<br />
                                    {date}
                                </th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {['Morning', 'Noon', 'Evening', 'Night'].map((shift) => (
                            <tr key={shift}>
                                <td>{shift}</td>
                                {weekDates.map((date) => (
                                    <td key={date}>
                                        <input
                                            type="checkbox"
                                            checked={availability[date]?.[shift] || false}
                                            onChange={(e) =>
                                                handleAvailabilityChange(date, shift, e.target.checked)
                                            }
                                        />
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
                <button onClick={handleSave} className="save-button">Save Availability</button>
            </div>
        </>
    );
};

export default AvailabilityScreen;
