import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Services
import { getWeeklyStats } from '../services/statsService';
import { getUsers } from '../services/userService';

// Utils
import { calculateWeekKey, calculateWeekRange } from '../utils/utils';

// Components
import Navbar from './Navbar';
import BackgroundWrapper from './Layouts/BackgroundWrapper';
import Loader from './Common/Loader';

// Styles
import '../styles/StatsScreen.css';

const StatsScreen = () => {
    const [userData, setUserData] = useState(null);
    const [weekOffset, setWeekOffset] = useState(0);
    const [weeklyStats, setWeeklyStats] = useState(null);
    const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);
    const [employees, setEmployees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (!storedUser) {
            navigate('/');
        } else {
            setUserData(storedUser);
            if (storedUser.user.role === 'employee') {
                setSelectedEmployeeId(storedUser.user.id);
            } else {
                fetchEmployees(storedUser.company.id);
            }
        }
    }, [navigate]);

    useEffect(() => {
        if (userData && selectedEmployeeId) {
            fetchWeeklyStats();
        }
    }, [userData, selectedEmployeeId, weekOffset]);

    const fetchEmployees = async (companyId) => {
        try {
            const users = await getUsers();
            const filtered = Object.entries(users)
                .map(([id, user]) => ({ id, ...user }))
                .filter(user =>
                    user.companyIds?.some(c => c.companyId === companyId)
                );
            setEmployees(filtered);
        } catch (error) {
            console.error('Error fetching employees:', error);
        }
    };

    const fetchWeeklyStats = async () => {
        const { company } = userData;
        const weekKey = calculateWeekKey(weekOffset + 1);
        const now = new Date();
        const year = now.getFullYear();
        const month = now.getMonth() + 1;

        try {
            const stats = await getWeeklyStats(company.id, year, month, weekKey, selectedEmployeeId);
            setWeeklyStats(stats);
        } catch (error) {
            console.error('Error loading stats:', error);
            setWeeklyStats(null);
        }
    };

    const handleWeekChange = (direction) => {
        setWeekOffset(prev => prev + direction);
    };

    const selectedEmployee = employees.find(e => e.id === selectedEmployeeId);

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div>
            <div className="stats-container">
                <h1>Weekly Stats</h1>

                {userData?.user.role === 'admin' && (
                    <div className="stats-dropdown">
                        <label>Select Employee:</label>
                        <select
                            value={selectedEmployeeId || ''}
                            onChange={(e) => setSelectedEmployeeId(e.target.value)}
                        >
                            <option value="">-- Choose an employee --</option>
                            {employees.map((e) => (
                                <option key={e.id} value={e.id}>{e.name}</option>
                            ))}
                        </select>
                    </div>
                )}

                {selectedEmployeeId && (
                    <>
                        <div className="week-navigation">
                            <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                            <h2>{calculateWeekRange(weekOffset + 1)}</h2>
                            <button onClick={() => handleWeekChange(1)}>Next Week</button>
                        </div>

                        {weeklyStats ? (
                            <div className="stats-data">
                                <p><strong>Total Shifts:</strong> {weeklyStats.totalShifts || 0}</p>
                                <p><strong>Morning Shifts:</strong> {weeklyStats.morning || 0}</p>
                                <p><strong>Noon Shifts:</strong> {weeklyStats.noon || 0}</p>
                                <p><strong>Evening Shifts:</strong> {weeklyStats.evening || 0}</p>
                                <p><strong>Night Shifts:</strong> {weeklyStats.night || 0}</p>
                                <p><strong>Vacation Days:</strong> {weeklyStats.vacationDays || 0}</p>
                                <p><strong>Notes:</strong> {weeklyStats.notes || 'None'}</p>
                            </div>
                        ) : (
                            <p>No statistics available for this week.</p>
                        )}
                    </>
                )}

                {!selectedEmployeeId && userData?.user.role === 'admin' && (
                    <p>Please select an employee to view stats.</p>
                )}

                {!userData && <Loader />}
            </div>
        </BackgroundWrapper>
    );
};

export default StatsScreen;
