import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import '../styles/AvailabilityScreen.css';

const AvailabilityScreen = () => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const shifts = ['Morning', 'Noon', 'Evening', 'Night'];

    const [availability, setAvailability] = useState(
        shifts.reduce((acc, shift) => {
            acc[shift] = daysOfWeek.reduce((dayAcc, day) => {
                dayAcc[day] = false;
                return dayAcc;
            }, {});
            return acc;
        }, {})
    );

    const [dateRange, setDateRange] = useState('');

    useEffect(() => {
        const calculateWeekRange = () => {
            const today = new Date();
            const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay()));
            const lastDayOfWeek = new Date(today.setDate(firstDayOfWeek.getDate() + 6));

            const formatDate = (date) => {
                const day = String(date.getDate()).padStart(2, '0');
                const month = String(date.getMonth() + 1).padStart(2, '0');
                const year = date.getFullYear();
                return `${day}/${month}/${year}`;
            };

            setDateRange(`${formatDate(firstDayOfWeek)} - ${formatDate(lastDayOfWeek)}`);
        };

        calculateWeekRange();
    }, []);

    const handleAvailabilityChange = (shift, day) => {
        setAvailability((prev) => ({
            ...prev,
            [shift]: {
                ...prev[shift],
                [day]: !prev[shift][day],
            },
        }));
    };

    const handleSubmit = () => {
        // פעולה עתידית - כרגע רק הדפסה
        console.log('Availability submitted:', availability);
    };

    return (
        <div className="availability-container">
            <Navbar />
            <h1>Weekly Availability</h1>
            <h2>Week: {dateRange}</h2>
            <table className="availability-table">
                <thead>
                    <tr>
                        <th>Shift</th>
                        {daysOfWeek.map((day) => (
                            <th key={day}>{day}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {shifts.map((shift) => (
                        <tr key={shift}>
                            <td>{shift}</td>
                            {daysOfWeek.map((day) => (
                                <td key={day}>
                                    <input
                                        type="checkbox"
                                        checked={availability[shift][day]}
                                        onChange={() => handleAvailabilityChange(shift, day)}
                                    />
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="submit-button" onClick={handleSubmit}>
                Submit
            </button>
        </div>
    );
};

export default AvailabilityScreen;
