import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { saveAvailability, getAvailability } from '../services/availabilityService';
import '../styles/AvailabilityScreen.css';

const AvailabilityScreen = () => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const shifts = ['Morning', 'Noon', 'Evening', 'Night'];

    const [availability, setAvailability] = useState(null); // Availability for the current week
    const [notes, setNotes] = useState(''); // Weekly notes
    const [currentWeek, setCurrentWeek] = useState(1); // Start with the next week
    const [userData, setUserData] = useState(null); // Logged-in user details
    const navigate = useNavigate();

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        const fetchAvailability = async () => {
            const companyId = userData?.company?.id;
            const userId = userData?.user?.id;
            const weekKey = calculateWeekKey(currentWeek);

            if (companyId && userId) {
                try {
                    const fetchedAvailability = await getAvailability(companyId, weekKey, userId);
                    setAvailability(
                        initializeAvailability(fetchedAvailability || {})
                    );
                    setNotes(fetchedAvailability?.notes || ''); // Load notes
                } catch (error) {
                    console.error('Error fetching availability:', error);
                    alert('Failed to fetch availability. Please try again.');
                }
            }
        };

        if (userData) {
            fetchAvailability();
        }
    }, [currentWeek, userData]);

    const calculateWeekKey = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));
        const formatDate = (date) => `${date.getFullYear()}_${String(date.getMonth() + 1).padStart(2, '0')}_${String(date.getDate()).padStart(2, '0')}`;
        return `week_${formatDate(firstDayOfWeek)}`;
    };

    const calculateDatesForWeek = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));

        return daysOfWeek.map((_, index) => {
            const day = new Date(firstDayOfWeek);
            day.setDate(day.getDate() + index);
            return day.toISOString().split('T')[0]; // Format as YYYY-MM-DD
        });
    };

    const initializeAvailability = (existingAvailability) => {
        return shifts.reduce((acc, shift) => {
            acc[shift] = daysOfWeek.reduce((dayAcc, day) => {
                dayAcc[day] = existingAvailability[shift]?.[day] || false;
                return dayAcc;
            }, {});
            return acc;
        }, {});
    };

    const handleAvailabilityChange = (shift, day) => {
        setAvailability((prev) => ({
            ...prev,
            [shift]: {
                ...prev[shift],
                [day]: !prev[shift][day],
            },
        }));
    };

    const handleWeekChange = (direction) => {
        setCurrentWeek((prevWeek) => prevWeek + direction);
    };

    const handleSubmit = async () => {
        const companyId = userData.company.id;
        const userId = userData.user.id;
        const weekKey = calculateWeekKey(currentWeek);
        const datesForWeek = calculateDatesForWeek(currentWeek);

        try {
            const availabilityWithDates = shifts.reduce((acc, shift) => {
                acc[shift] = daysOfWeek.reduce((dayAcc, day, index) => {
                    dayAcc[datesForWeek[index]] = availability[shift][day];
                    return dayAcc;
                }, {});
                return acc;
            }, {});

            const dataToSave = {
                availability: availabilityWithDates,
                notes,
            };

            await saveAvailability(companyId, weekKey, userId, dataToSave);
            alert('Availability and notes saved successfully!');
        } catch (error) {
            console.error('Error saving availability or notes:', error);
            alert('Failed to save availability or notes. Please try again.');
        }
    };

    if (!userData || !availability) {
        return <p>Loading...</p>;
    }

    return (
        <div className="availability-container">
            <Navbar />
            <h1>Weekly Availability</h1>
            <div className="week-navigation">
                <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                <h2>{calculateWeekKey(currentWeek)}</h2>
                <button onClick={() => handleWeekChange(1)}>Next Week</button>
            </div>
            <table className="availability-table">
                <thead>
                    <tr>
                        <th>Shift</th>
                        {daysOfWeek.map((day) => (
                            <th key={day}>{day}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {shifts.map((shift) => (
                        <tr key={shift}>
                            <td>{shift}</td>
                            {daysOfWeek.map((day) => (
                                <td key={day}>
                                    <input
                                        type="checkbox"
                                        checked={availability[shift][day]}
                                        onChange={() => handleAvailabilityChange(shift, day)}
                                    />
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
            <textarea
                className="notes-textarea"
                placeholder="Write weekly notes here..."
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
            ></textarea>
            <button className="submit-button" onClick={handleSubmit}>
                Submit
            </button>
        </div>
    );
};

export default AvailabilityScreen;
