// External libraries
import React, { useState, useEffect } from 'react';

// Components
import Navbar from '../components/Navbar';
import Loader from './Common/Loader';
import BackgroundWrapper from './Layouts/BackgroundWrapper';
import EmployeeCard from '../components/EmployeeCard';

// Styles
import '../styles/EmployeesScreen.css';
import '../styles/Navbar.css';

// Services
import { fetchEmployees } from '../utils/employeeHandlers';

// Utils
import { filterEmployees } from '../utils/employeeUtils';

function PhonebookScreen() {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [loggedUser, setLoggedUser] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [filterRole, setFilterRole] = useState('all');
    const [openCards, setOpenCards] = useState({});

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'));
        setLoggedUser(user);
    }, []);

    useEffect(() => {
        if (loggedUser) {
            const companyId = loggedUser?.company?.id;
            if (companyId) {
                fetchEmployees(companyId, setEmployees, setError, setIsLoading);
            }
        }
    }, [loggedUser]);

    const toggleCard = (id) => {
        setOpenCards((prev) => ({
            ...prev,
            [id]: !prev[id],
        }));
    };

    const filteredEmployees = filterEmployees(employees, searchQuery, filterRole);

    return (
        <BackgroundWrapper>
            <div>
                <Navbar />
                <div className="navbar-placeholder"></div>
                <div className="employees-container">
                    <h1>Phonebook</h1>

                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search by name or email..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <select value={filterRole} onChange={(e) => setFilterRole(e.target.value)}>
                            <option value="all">All Roles</option>
                            <option value="employee">Employee</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>

                    {isLoading ? (
                        <Loader />
                    ) : error ? (
                        <p className="error">{error}</p>
                    ) : (
                        <div className="employees-cards">
                            {[...filteredEmployees]
                                .sort((a, b) => a.name.localeCompare(b.name, 'he', { sensitivity: 'base' }))
                                .map((employee) => (
                                    <EmployeeCard
                                        key={employee.id}
                                        employee={employee}
                                        isOpen={openCards[employee.id]}
                                        onToggle={toggleCard}
                                        onEdit={null} // ❌ לא עורך
                                        onDelete={null} // ❌ לא מוחק
                                    />
                                ))}
                        </div>
                    )}
                </div>
            </div>
        </BackgroundWrapper>
    );
}

export default PhonebookScreen;
