import axios from 'axios';

const API_URL = 'http://localhost:5000/api/users';

// Get all users
export const getUsers = async () => {
    try {
        const response = await axios.get(API_URL);
        return response.data;
    } catch (error) {
        console.error('Error fetching users:', error.message);
        throw error;
    }
};

// Get a user by ID
export const getUserById = async (id) => {
    try {
        const response = await axios.get(`${API_URL}/${id}`);
        return response.data;
    } catch (error) {
        console.error(`Error fetching user with ID ${id}:`, error.message);
        throw error;
    }
};

// Add a new user
export const addUser = async (userData) => {
    try {
        const response = await axios.post(API_URL, userData);
        return response.data;
    } catch (error) {
        console.error('Error adding user:', error.message);
        throw error;
    }
};

// Update a user by ID
export const updateUser = async (id, updatedData) => {
    try {
        const response = await axios.put(`${API_URL}/${id}`, updatedData);
        return response.data;
    } catch (error) {
        console.error(`Error updating user with ID ${id}:`, error.message);
        throw error;
    }
};

// Update user companies
export const updateUserCompanies = async (user, company) => {
    try {
        console.log("6666666666666", user);
        console.log("6666666666666", user);

        // Fetch the current user data
        // const user = await getUserById(userId);

        // Add the new company to the user's list of companies
        const updatedCompanies = user.companies ? [...user.companies, company.id] : [company.id];

        // Update the user with the new list of companies
        const updatedData = { ...user, companies: updatedCompanies };
        const response = await updateUser(user.id, updatedData);

        return response.data;
    } catch (error) {
        console.error(`Error updating companies for user with ID ${user.id}:`, error.message);
        throw error;
    }
};

// Delete a user by ID
export const deleteUser = async (id) => {
    try {
        const response = await axios.delete(`${API_URL}/${id}`);
        return response.data;
    } catch (error) {
        console.error(`Error deleting user with ID ${id}:`, error.message);
