// External libraries
import Modal from 'react-modal';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const VacationRequestModal = ({
    isOpen,
    onClose,
    vacationStartDate,
    vacationEndDate,
    setVacationStartDate,
    setVacationEndDate,
    handleVacationRequestSubmit,
}) => {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onClose}
            className="modal"
            overlayClassName="modal-overlay"
        >
            <h3>Vacation Request</h3>
            <form onSubmit={handleVacationRequestSubmit} className="addOrEdit-employee-form">
                <label>Select Vacation Range:</label>
                <DatePicker
                    selectsRange
                    startDate={vacationStartDate}
                    endDate={vacationEndDate}
                    onChange={([start, end]) => {
                        setVacationStartDate(start);
                        setVacationEndDate(end);
                    }}
                    dateFormat="dd/MM/yyyy"
                    placeholderText="Select vacation dates"
                    className="datepicker"
                    isClearable
                />

                <div style={{ marginTop: '20px', textAlign: 'center' }}>
                    <button type="submit" className="submit-button">Submit</button>
                    <button
                        type="button"
                        onClick={onClose}
                        className="cancel-button"
                    >
                        Cancel
                    </button>
                </div>
            </form>
        </Modal>
    );
};

export default VacationRequestModal;
