// React functions
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

// Third-party libraries
import { toast, ToastContainer } from 'react-toastify';

// Styles
import 'react-toastify/dist/ReactToastify.css';
import '../styles/ChatScreen.css';
import '../styles/Navbar.css';

// Services
import { getGroupMessages, sendGroupMessage } from '../services/chatService';

// Internal components
import Navbar from './Navbar';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';

const ChatScreen = () => {
    const navigate = useNavigate();

    const [userData, setUserData] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    const messagesEndRef = useRef(null);

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);
        }
    }, [navigate]);

    useEffect(() => {
        if (userData?.company?.id) {
            fetchMessages();
        }
    }, [userData]);

    const fetchMessages = async () => {
        try {
            const data = await getGroupMessages(userData.company.id);
            const messagesArray = data ? Object.values(data).sort((a, b) => a.timestamp - b.timestamp) : [];
            setMessages(messagesArray);
        } catch (error) {
            console.error('Error fetching group messages:', error);
            toast.error('Failed to load messages.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleSendMessage = async () => {
        if (!newMessage.trim()) return;

        const messageData = {
            senderId: userData.user.id,
            senderName: userData.user.name || 'Unknown', // ✅ שימוש עקבי בשם המלא
            content: newMessage.trim(),
            timestamp: Date.now(),
        };

        try {
            await sendGroupMessage(userData.company.id, messageData);
            setMessages((prev) => [...prev, messageData]);
            setNewMessage('');
            scrollToBottom();
        } catch (error) {
            toast.error('Failed to send message.');
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(scrollToBottom, [messages]);

    if (!userData) {
        return <Loader />;
    }

    const isSameDate = (timestamp1, timestamp2) => {
        const date1 = new Date(timestamp1);
        const date2 = new Date(timestamp2);
        return (
            date1.getFullYear() === date2.getFullYear() &&
            date1.getMonth() === date2.getMonth() &&
            date1.getDate() === date2.getDate()
        );
    };

    return (
        <BackgroundWrapper>
            <Navbar />
            <div className="navbar-placeholder"></div>

            <div className="chat-container">
                <h1>Group Chat</h1>

                {isLoading ? (
                    <Loader />
                ) : (
                        <>
                            <div className="chat-messages">

                                {messages.map((msg, index) => {
                                    const currentDate = new Date(msg.timestamp);
                                    const prevDate = index > 0 ? new Date(messages[index - 1].timestamp) : null;

                                    const showDate =
                                        index === 0 || !isSameDate(currentDate, prevDate);

                                    return (
                                        <React.Fragment key={index}>
                                            {showDate && (
                                                <div className="date-header-wrapper">
                                                    <div className="date-header">
                                                        {currentDate.toLocaleDateString('he-IL', {
                                                            weekday: 'short',
                                                            day: '2-digit',
                                                            month: '2-digit',
                                                            year: 'numeric',
                                                        })}
                                                    </div>
                                                </div>
                                            )}

                                            <div
                                                className={`chat-message-wrapper ${msg.senderId === userData.user.id ? 'align-right' : 'align-left'}`}
                                            >
                                                <div className={`chat-message ${msg.senderId === userData.user.id ? 'my-message' : 'other-message'}`}>
                                                    <div className="chat-sender">{msg.senderName}</div>
                                                    <div className="chat-content">{msg.content}</div>
                                                    <div className="chat-time">
                                                        {new Date(msg.timestamp).toLocaleTimeString('he-IL', {
                                                            hour: '2-digit',
                                                            minute: '2-digit',
                                                        })}
                                                    </div>
                                                </div>
                                            </div>
                                        </React.Fragment>
                                    );
                                })}

                                <div ref={messagesEndRef} />
                            </div>

                            <div className="chat-input">
                                <input
                                    type="text"
                                    placeholder="Type a message..."
                                    value={newMessage}
                                    onChange={(e) => setNewMessage(e.target.value)}
                                    onKeyDown={handleKeyPress}
                                />
                                <button onClick={handleSendMessage}>Send</button>
                            </div>
                        </>

                )}

                <ToastContainer />
            </div>
        </BackgroundWrapper>
    );
};

export default ChatScreen;
