import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/HomeScreen.css';
import '../styles/Navbar.css';

function HomeScreen() {
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // שליפת הנתונים מ-localStorage
        const loggedUser = JSON.parse(localStorage.getItem('user'));

        if (!loggedUser) {
            navigate('/'); // אם המשתמש לא מחובר, נוודא שהוא חוזר למסך ההתחברות
        } else {
            setUserData(loggedUser); // שומר את פרטי המשתמש והחברה ב-state
        }
    }, [navigate]);

    if (!userData) {
        return <p>Loading...</p>; // תצוגת טעינה עד שהנתונים נטענים
    }

    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* מקום עבור ה-navbar */}

            <div className="home-container">
                <div className="home-content">
                    <h1>Welcome, {userData.user.name}!</h1>
                    <p>Company: {userData.company ? userData.company.name : "N/A"}</p>
                    <div className="home-actions">
                        <button className="action-button" onClick={() => navigate('/profile')}>Go to Profile</button>
                        <button className="action-button" onClick={() => navigate('/')}>Logout</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default HomeScreen;
