import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/ProfileScreen.css';
import '../styles/Navbar.css';

const ProfileScreen = () => {
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // שליפת פרטי המשתמש והחברה מה-localStorage
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/'); // אם המשתמש לא מחובר, נוודא שהוא חוזר למסך ההתחברות
        } else {
            setUserData(loggedUser); // שומר את פרטי המשתמש והחברה ב-state
        }
    }, [navigate]);

    if (!userData) {
        return <p>Loading...</p>; // תצוגת טעינה עד שהנתונים נטענים
    }

    return (
        <>
            <Navbar /> {/* הוצא את ה-Navbar מחוץ ל-profile-container */}
            <div className="profile-container">
                <div className="profile-info">
                    {/* הצגת פרטי המשתמש */}
                    <h2>Welcome, {userData.user.name}</h2>
                    <p>Email: {userData.user.email}</p>
                    <p>Company: {userData.company ? userData.company.name : "N/A"}</p>
                    <p>Role: {userData.user.role}</p>
                    <button onClick={() => navigate('/home')}>Back to Home</button>
                </div>
            </div>
        </>
    );

}

export default ProfileScreen;
