// React & state
import React, { useState, useEffect, useRef } from 'react';

// UI Components & Libraries
import Modal from 'react-modal';
import { toast, ToastContainer } from 'react-toastify';
import { ThreeDots } from 'react-loader-spinner';
import Navbar from '../components/Navbar';
import Loader from './Loader';
import BackgroundWrapper from './BackgroundWrapper';


// Styles
import 'react-toastify/dist/ReactToastify.css';
import '../styles/EmployeesScreen.css';
import '../styles/Navbar.css';

// Data
import employeesData from '../data/emps.json'; // יש לוודא שהקובץ נמצא בתיקייה data

// Services
import { getUsers, removeCompanyFromUser, addUser, updateUserCompanies, updateUser } from '../services/userService';
import { removeUserFromCompany, addEmployeeToCompany } from '../services/companyService';

Modal.setAppElement('#root');

function EmployeesScreen() {

    // Employees data
    const [employees, setEmployees] = useState([]);

    // Form data
    const [newEmployee, setNewEmployee] = useState({
        name: '', email: '', phone: '', role: 'employee', password: '', photoFile: null
    });

    // App state
    const [isLoading, setIsLoading] = useState(true);
    const [isProcessing, setIsProcessing] = useState(false);
    const [error, setError] = useState('');
    const [loggedUser, setLoggedUser] = useState(null);

    // Modals
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isConfirmDeleteModalOpen, setIsConfirmDeleteModalOpen] = useState(false);

    // Edit/delete handlers
    const [editEmployee, setEditEmployee] = useState(null);
    const [employeeToDelete, setEmployeeToDelete] = useState(null);

    // UI helpers
    const [openCards, setOpenCards] = useState({});
    const [searchQuery, setSearchQuery] = useState('');
    const [filterRole, setFilterRole] = useState('all');

    const fileInputRef = useRef(null);
    const editFileInputRef = useRef(null); // ⬅️ הוסף את זה!

    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        console.log("🔐 Logged user from localStorage:", loggedUser);
        setLoggedUser(loggedUser);
    }, []);


    // On component mount, fetch the list of employees associated with the logged-in user's company:
    // - Retrieve the logged-in user from localStorage.
    // - Extract the company ID and validate it.
    // - Fetch all users from the database.
    // - Filter users to include only those who belong to the current company.
    // - Update the local employee list state.
    // - Handle loading and error states appropriately.
    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const loggedUser = JSON.parse(localStorage.getItem('user'));
                setLoggedUser(loggedUser);
                const companyId = loggedUser?.company?.id;

                if (!companyId) {
                    setError('No company information found.');
                    return;
                }

                const data = await getUsers();
                const usersWithIds = data ? Object.entries(data).map(([id, user]) => ({ id, ...user })) : [];
                const filteredEmployees = usersWithIds.filter((user) =>
                    user.companyIds?.some((company) => company.companyId === companyId)
                );

                setEmployees(filteredEmployees);
            } catch (err) {
                setError('Failed to fetch employees.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchEmployees();
    }, []);

    // Open the confirmation modal for deleting an employee.
    // Stores the selected employee in state for later use.
    const openConfirmDeleteModal = (employee) => {
        setEmployeeToDelete(employee);
        setIsConfirmDeleteModalOpen(true);
    };

    // Close the confirmation modal and clear the selected employee.
    const closeConfirmDeleteModal = () => {
        setIsConfirmDeleteModalOpen(false);
        setEmployeeToDelete(null);
    };

    // Handle confirmed deletion of an employee:
    // - Validate company information.
    // - Remove the employee from the user and company records.
    // - Update the UI to reflect the deletion.
    // - Display success or error notifications.
    const handleDeleteConfirmed = async () => {
        if (!employeeToDelete) return;
        try {
            const companyId = loggedUser?.company?.id;

            if (!companyId) {
                toast.error('No company information found. Please try again.');
                return;
            }

            await removeCompanyFromUser(employeeToDelete.id, companyId);
            await removeUserFromCompany(companyId, employeeToDelete.id);

            setEmployees(employees.filter((employee) => employee.id !== employeeToDelete.id));
            toast.success('Employee deleted successfully!');
        } catch (err) {
            toast.error('Failed to delete employee.');
            console.error(err);
        } finally {
            closeConfirmDeleteModal();
        }
    };

    // Handle input changes in the "Add New Employee" form.
    // Updates the corresponding field in the newEmployee state.
    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewEmployee((prev) => ({ ...prev, [name]: value }));
    };

    // Handle submission of the "Add New Employee" form:
    // - Prevent default form submission.
    // - Check for valid company information.
    // - Check if a user with the given email already exists:
    //     - If yes, associate them with the current company.
    //     - If not, create a new user and associate them with the company.
    // - Update the employees list in the UI accordingly.
    // - Show success or error toasts.
    // - Reset the form and close the modal after successful addition.
    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            const company = loggedUser?.company;

            if (!company) {
                toast.error('No company information found. Please try again.');
                return;
            }

            // 1. המרת קובץ תמונה ל־Base64 (data URL)
            let photoDataUrl = '';
            if (newEmployee.photoFile) {
                const reader = new FileReader();
                const file = newEmployee.photoFile;

                photoDataUrl = await new Promise((resolve, reject) => {
                    reader.onloadend = () => resolve(reader.result);
                    reader.onerror = reject;
                    reader.readAsDataURL(file);
                });
            }

            const allUsers = await getUsers();
            const existingUser = Object.values(allUsers).find((user) => user.email === newEmployee.email);

            if (existingUser) {
                await updateUserCompanies(existingUser, company);
                await addEmployeeToCompany(company.id, existingUser.id);

                const isUserInUI = employees.some((employee) => employee.id === existingUser.id);
                if (!isUserInUI) {
                    setEmployees((prev) => [...prev, existingUser]);
                }

                toast.info('User already exists, added to the company successfully.');
            } else {
                // 2. הוספת משתמש כולל תמונה
                const addedEmployee = await addUser({
                    ...newEmployee,
                    photoData: photoDataUrl // ⬅️ התמונה בבסיס הנתונים
                });

                await updateUserCompanies(addedEmployee, company);
                await addEmployeeToCompany(company.id, addedEmployee.id);

                setEmployees((prev) => [...prev, { id: addedEmployee.id, ...addedEmployee }]);
                toast.success('New user added successfully!');
            }

            // 3. איפוס הטופס
            setNewEmployee({ name: '', email: '', phone: '', role: 'employee', password: '', photoFile: null });
            setIsModalOpen(false);
        } catch (err) {
            toast.error('Failed to add employee.');
            console.error(err);
        }
    };


    // Toggle the expanded/collapsed state of an employee card by its ID.
    // If the card is currently open, it will be closed, and vice versa.
    const toggleCard = (id) => {
        setOpenCards((prev) => ({
            ...prev,
            [id]: !prev[id],
        }));
    };

    const handleEditClick = (employee) => {
        setEditEmployee(employee); // הגדרת העובד הנבחר לעריכה
        setIsEditModalOpen(true); // פתיחת המודל
    };


    const handleLoadEmployeesFromJSON = async () => {
        try {
            if (!loggedUser || !loggedUser.company) {
                toast.error('No company information found. Please try again.');
                return;
            }

            setIsProcessing(true); // הפעלת ה-Loader

            const companyId = loggedUser.company.id;
            const allUsers = await getUsers();

            const newEmployees = employeesData.filter((employee) => {
                const existingUser = Object.values(allUsers).find((user) => user.email === employee.email);

                if (!existingUser) {
                    return true; // משתמש לא קיים כלל - נוסיף אותו
                }

                const isInCompany = existingUser.companyIds?.some((company) => company.companyId === companyId);
                return !isInCompany; // אם הוא לא משויך לחברה, נוסיף אותו שוב
            });

            for (const employee of newEmployees) {
                const existingUser = Object.values(allUsers).find((user) => user.email === employee.email);

                if (existingUser) {
                    await updateUserCompanies(existingUser, loggedUser.company);
                    await addEmployeeToCompany(companyId, existingUser.id);
                } else {
                    const addedEmployee = await addUser(employee);
                    await updateUserCompanies(addedEmployee, loggedUser.company);
                    await addEmployeeToCompany(companyId, addedEmployee.id);
                }
            }

            const updatedUsers = await getUsers();
            const usersWithIds = Object.entries(updatedUsers).map(([id, user]) => ({ id, ...user }));
            const filteredEmployees = usersWithIds.filter((user) =>
                user.companyIds?.some((company) => company.companyId === companyId)
            );

            setEmployees(filteredEmployees);
            toast.success(`${newEmployees.length} employees added successfully!`);
        } catch (error) {
            toast.error('Failed to load employees from JSON.');
            console.error(error);
        } finally {
            setIsProcessing(false); // כיבוי ה-Loader
        }
    };

    // Delete all non-admin employees from the current company:
    // - Validate that the logged-in user and company info exist.
    // - Show a loader while processing.
    // - Filter out all employees who are not admins.
    // - For each employee, remove their association with the company both in user and company records.
    // - Update the UI to show only admins.
    // - Display appropriate success or error messages.
    const handleDeleteAllEmployees = async () => {
        try {
            if (!loggedUser || !loggedUser.company) {
                toast.error('No company information found. Please try again.');
                return;
            }

            setIsProcessing(true); // הפעלת ה-Loader

            const companyId = loggedUser.company.id;
            const employeesToDelete = employees.filter(employee => employee.role !== 'admin');

            if (employeesToDelete.length === 0) {
                toast.info('No employees to delete.');
                return;
            }

            for (const employee of employeesToDelete) {
                await removeCompanyFromUser(employee.id, companyId);
                await removeUserFromCompany(companyId, employee.id);
            }

            setEmployees(employees.filter(employee => employee.role === 'admin'));
            toast.success(`${employeesToDelete.length} employees deleted successfully!`);
        } catch (err) {
            toast.error('Failed to delete employees.');
            console.error(err);
        } finally {
            setIsProcessing(false); // כיבוי ה-Loader
        }
    };

    const filteredEmployees = employees.filter((employee) => {
        return (
            (employee.name.toLowerCase().startsWith(searchQuery.toLowerCase()) ||
                employee.email.toLowerCase().startsWith(searchQuery.toLowerCase())) &&
            (filterRole === 'all' || employee.role === filterRole)
        );
    });

    // Render the EmployeesScreen UI:
    // - Navbar at the top with a placeholder to maintain layout spacing.
    // - A search bar and role filter for filtering employees by name/email and role.
    // - Conditional rendering based on loading/error state:
    //     - If loading: show loading message.
    //     - If error: show error message.
    //     - Else: display a grid of employee cards, each toggleable to show details (email, phone, role),
    //       with Edit/Delete options depending on role.
    // - Action buttons below the list for:
    //     - Adding a new employee via form modal.
    //     - Loading multiple employees from a JSON file.
    //     - Deleting all non-admin employees.
    // - Modals for:
    //     - Confirming deletion of a single employee.
    //     - Adding a new employee (form).
    //     - Editing an existing employee (form).
    // - Toast notifications container at the bottom for success/error messages.
    return (
        <BackgroundWrapper >

            <div>
                <Navbar />
                <div className="navbar-placeholder"></div>
                <div className="employees-container">
                    <h1>Employees</h1>

                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search by name or email..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <select value={filterRole} onChange={(e) => setFilterRole(e.target.value)}>
                            <option value="all">All Roles</option>
                            <option value="employee">Employee</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>

                    {isLoading ? (
                        <Loader />
                    ) : error ? (
                        <p className="error">{error}</p>
                    ) : (
                        <div className="employees-cards">
                            {filteredEmployees.map((employee) => (

                                <div
                                    key={employee.id}
                                    className={`employee-card ${openCards[employee.id] ? 'open' : 'closed'}`}
                                    onClick={() => toggleCard(employee.id)}
                                >
                                    <h3>{employee.name}</h3>
                                    {openCards[employee.id] && (
                                        <div className="employee-details-horizontal">
                                            <img
                                                src={employee.photoData || '/images/Profile.jpeg'}
                                                alt={`${employee.name}'s profile`}
                                                className="employee-photo-horizontal"
                                            />

                                            <div className="employee-info">
                                                <p>Email: {employee.email}</p>
                                                <p>Phone: {employee.phone}</p>
                                                <p>Role: {employee.role}</p>
                                                <div className="employee-actions">
                                                    <button
                                                        className="action-button edit"
                                                        onClick={(e) => {
                                                            e.stopPropagation();
                                                            handleEditClick(employee);
                                                        }}
                                                    >
                                                        Edit
                                                    </button>
                                                    {employee.role !== 'admin' && (
                                                        <button
                                                            className="action-button delete"
                                                            onClick={(e) => {
                                                                e.stopPropagation();
                                                                openConfirmDeleteModal(employee);
                                                            }}
                                                        >
                                                            Delete
                                                        </button>
                                                    )}
                                                </div>
                                            </div>
                                        </div>

                                    )}

                                </div>
                            ))}

                        </div>
                    )}

                    {isProcessing && (
                        <ThreeDots
                            height="50"
                            width="50"
                            radius="9"
                            color="#3498db"
                            ariaLabel="processing"
                            wrapperStyle={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}
                            visible={true}
                        />
                    )}

                    <button
                        className="toggle-form-button"
                        onClick={() => setIsModalOpen(true)}
                    >
                        Add New Employee
                    </button>
                    {/* 🔹 כפתור חדש להוספת עובדים מקובץ JSON */}
                    <button className="toggle-form-button" onClick={handleLoadEmployeesFromJSON}>
                        Add Employees from JSON
                    </button>
                    <button
                        className="toggle-form-button delete-all"
                        onClick={handleDeleteAllEmployees}
                    >
                        Delete All Non-Admin Employees
                    </button>
                    <Modal
                        isOpen={isConfirmDeleteModalOpen}
                        onRequestClose={closeConfirmDeleteModal}
                        className="confirm-delete-modal"
                        overlayClassName="modal-overlay"
                    >
                        <h2>Confirm Employee Deletion</h2>
                        <p>
                            Are you sure you want to remove <strong>{employeeToDelete?.name}</strong> from the company?
                        </p>
                        <div className="modal-buttons">
                            <button className="confirm-button" onClick={handleDeleteConfirmed}>
                                Confirm
                            </button>
                            <button className="cancel-button" onClick={closeConfirmDeleteModal}>
                                Cancel
                            </button>
                        </div>
                    </Modal>


                    <Modal
                        isOpen={isModalOpen}
                        onRequestClose={() => setIsModalOpen(false)}
                        className="modal"
                        overlayClassName="modal-overlay"
                    >
                        <h3>Add New Employee</h3>
                        <form className="addOrEdit-employee-form" onSubmit={handleAddEmployee}>
                            <label>Name:</label>
                            <input
                                type="text"
                                name="name"
                                value={newEmployee.name}
                                onChange={handleChange}
                                required
                            />
                            <label>Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={newEmployee.email}
                                onChange={handleChange}
                                required
                            />
                            <label>Phone:</label>
                            <input
                                type="text"
                                name="phone"
                                value={newEmployee.phone}
                                onChange={handleChange}
                                required
                            />
                            <label>Password:</label>
                            <input
                                type="password"
                                name="password"
                                value={newEmployee.password}
                                onChange={handleChange}
                                required
                            />
                            <label>Role:</label>
                            <select name="role" value={newEmployee.role} onChange={handleChange}>
                                <option value="employee">Employee</option>
                                <option value="admin">Admin</option>
                            </select>
                            <label>Profile Photo:</label>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) =>
                                    setNewEmployee((prev) => ({
                                        ...prev,
                                        photoFile: e.target.files[0]
                                    }))
                                }
                            />

                            {newEmployee.photoFile && (
                                <img
                                    src={URL.createObjectURL(newEmployee.photoFile)}
                                    alt="Preview"
                                    style={{
                                        width: '100px',
                                        height: '100px',
                                        objectFit: 'cover',
                                        borderRadius: '8px',
                                        margin: '10px 0'
                                    }}
                                />
                            )}


                            <button type="submit">Add Employee</button>
                            <button
                                type="button"
                                onClick={() => setIsModalOpen(false)}
                                className="cancel-button"
                            >
                                Cancel
                            </button>
                        </form>
                    </Modal>
                    <Modal
                        isOpen={isEditModalOpen}
                        onRequestClose={() => setIsEditModalOpen(false)}
                        className="modal"
                        overlayClassName="modal-overlay"
                    >
                        <h3>Edit Employee</h3>
                        <form
                            className="addOrEdit-employee-form"
                            onSubmit={async (e) => {
                                e.preventDefault();
                                try {
                                    if (editEmployee && editEmployee.id) {
                                        // המרת תמונה חדשה ל־Base64, אם קיימת
                                        let updatedPhotoData = editEmployee.photoData || '';
                                        if (editEmployee.photoFile) {
                                            const reader = new FileReader();
                                            updatedPhotoData = await new Promise((resolve, reject) => {
                                                reader.onloadend = () => resolve(reader.result);
                                                reader.onerror = reject;
                                                reader.readAsDataURL(editEmployee.photoFile);
                                            });
                                        }

                                        // קריאה ל-updateUser עם הפרטים המעודכנים כולל תמונה
                                        const updatedEmployee = await updateUser(editEmployee.id, {
                                            name: editEmployee.name,
                                            email: editEmployee.email,
                                            phone: editEmployee.phone,
                                            role: editEmployee.role,
                                            photoData: updatedPhotoData,
                                        });

                                        // עדכון רשימת העובדים ב-UI
                                        setEmployees((prevEmployees) =>
                                            prevEmployees.map((employee) =>
                                                employee.id === updatedEmployee.id ? updatedEmployee : employee
                                            )
                                        );

                                        toast.success('Employee updated successfully!');
                                    }

                                    setIsEditModalOpen(false); // סגירת המודל
                                } catch (err) {
                                    toast.error('Failed to update employee. Please try again.');
                                    console.error(err);
                                }
                            }}

                        >

                            <label>Name:</label>
                            <input
                                type="text"
                                name="name"
                                value={editEmployee?.name || ''}
                                onChange={(e) =>
                                    setEditEmployee((prev) => ({ ...prev, name: e.target.value }))
                                }
                                required
                            />
                            <label>Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={editEmployee?.email || ''}
                                onChange={(e) =>
                                    setEditEmployee((prev) => ({ ...prev, email: e.target.value }))
                                }
                                required
                            />
                            <label>Phone:</label>
                            <input
                                type="text"
                                name="phone"
                                value={editEmployee?.phone || ''}
                                onChange={(e) =>
                                    setEditEmployee((prev) => ({ ...prev, phone: e.target.value }))
                                }
                                required
                            />
                            <label>Role:</label>
                            <select
                                name="role"
                                value={editEmployee?.role || ''}
                                onChange={(e) =>
                                    setEditEmployee((prev) => ({ ...prev, role: e.target.value }))
                                }
                            >
                                <option value="employee">Employee</option>
                                <option value="admin">Admin</option>
                            </select>
                            <label>Profile Photo:</label>
                            <input
                                type="file"
                                accept="image/*"
                                ref={editFileInputRef}
                                onChange={(e) =>
                                    setEditEmployee((prev) => ({
                                        ...prev,
                                        photoFile: e.target.files[0]
                                    }))
                                }
                            />

                            {/* תצוגה מקדימה של תמונה חדשה */}
                            {editEmployee?.photoFile && (
                                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'start' }}>
                                    <img
                                        src={URL.createObjectURL(editEmployee.photoFile)}
                                        alt="Preview"
                                        style={{
                                            width: '100px',
                                            height: '100px',
                                            objectFit: 'cover',
                                            borderRadius: '8px',
                                            margin: '10px 0'
                                        }}
                                    />
                                    <button
                                        type="button"
                                        className="secondary-button"
                                        onClick={() => {
                                            editFileInputRef.current.value = '';
                                            setEditEmployee((prev) => ({ ...prev, photoFile: null }));
                                        }}
                                    >
                                        Remove Photo
                                    </button>
                                </div>
                            )}

                            {/* תצוגת תמונה קיימת, אם אין חדשה */}
                            {!editEmployee?.photoFile && editEmployee?.photoData && (
                                <img
                                    src={editEmployee.photoData}
                                    alt="Current"
                                    style={{
                                        width: '100px',
                                        height: '100px',
                                        objectFit: 'cover',
                                        borderRadius: '8px',
                                        margin: '10px 0'
                                    }}
                                />
                            )}


                            <button type="submit">Save Changes</button>
                            <button
                                type="button"
                                onClick={() => setIsEditModalOpen(false)}
                                className="cancel-button"
                            >
                                Cancel
                            </button>
                        </form>
                    </Modal>

                </div>
                <ToastContainer />
            </div>
        </BackgroundWrapper >

    );
}

export default EmployeesScreen;
