import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { saveAvailability, getAvailability } from '../services/availabilityService';
import '../styles/AvailabilityScreen.css';

const AvailabilityScreen = () => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const shifts = ['Morning', 'Noon', 'Evening', 'Night'];

    const [availability, setAvailability] = useState(null); // זמינות עבור השבוע הנוכחי
    const [currentWeek, setCurrentWeek] = useState(1); // מתחילים מהשבוע הבא
    const [userData, setUserData] = useState(null); // פרטי המשתמש
    const navigate = useNavigate();

    useEffect(() => {
        // שליפת פרטי המשתמש מ-localStorage
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/'); // אם המשתמש לא מחובר, נוודא שהוא חוזר למסך ההתחברות
        } else {
            setUserData(loggedUser); // שומר את פרטי המשתמש
        }
    }, [navigate]);

    useEffect(() => {
        // טעינת זמינות כאשר `currentWeek` משתנה
        const fetchAvailability = async () => {
            const companyId = userData?.company?.id;
            const userId = userData?.user?.id;
            const weekKey = calculateWeekKey(currentWeek);

            if (companyId && userId) {
                try {
                    const fetchedAvailability = await getAvailability(companyId, weekKey, userId);
                    setAvailability(
                        initializeAvailability(fetchedAvailability || {}) // אם אין נתונים, טבלה ריקה
                    );
                } catch (error) {
                    console.error('Error fetching availability:', error);
                    alert('Failed to fetch availability. Please try again.');
                }
            }
        };

        if (userData) {
            fetchAvailability();
        }
    }, [currentWeek, userData]);

    const calculateWeekKey = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));
        const formatDate = (date) => `${date.getFullYear()}_${String(date.getMonth() + 1).padStart(2, '0')}_${String(date.getDate()).padStart(2, '0')}`;
        return `week_${formatDate(firstDayOfWeek)}`;
    };

    const calculateWeekRange = (weekOffset) => {
        const today = new Date();
        const firstDayOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + weekOffset * 7));
        const lastDayOfWeek = new Date(firstDayOfWeek);
        lastDayOfWeek.setDate(firstDayOfWeek.getDate() + 6);

        const formatDate = (date) => {
            const day = String(date.getDate()).padStart(2, '0');
            const month = String(date.getMonth() + 1).padStart(2, '0');
            const year = date.getFullYear();
            return `${day}/${month}/${year}`;
        };

        return `${formatDate(firstDayOfWeek)} - ${formatDate(lastDayOfWeek)}`;
    };

    const initializeAvailability = (existingAvailability) => {
        // אתחול זמינות לכל המשמרות והימים
        return shifts.reduce((acc, shift) => {
            acc[shift] = daysOfWeek.reduce((dayAcc, day) => {
                dayAcc[day] = existingAvailability[shift]?.[day] || false;
                return dayAcc;
            }, {});
            return acc;
        }, {});
    };

    const handleAvailabilityChange = (shift, day) => {
        setAvailability((prev) => ({
            ...prev,
            [shift]: {
                ...prev[shift],
                [day]: !prev[shift][day],
            },
        }));
    };

    const handleWeekChange = (direction) => {
        // שינוי השבוע הנוכחי בלבד
        setCurrentWeek((prevWeek) => prevWeek + direction);
    };

    const handleSubmit = async () => {
        const companyId = userData.company.id;
        const userId = userData.user.id;
        const weekKey = calculateWeekKey(currentWeek);

        try {
            await saveAvailability(companyId, weekKey, userId, availability);
            alert('Availability saved successfully!');
        } catch (error) {
            console.error('Error saving availability:', error);
            alert('Failed to save availability. Please try again.');
        }
    };

    if (!userData || !availability) {
        return <p>Loading...</p>;
    }

    return (
        <div className="availability-container">
            <Navbar />
            <h1>Weekly Availability</h1>
            <div className="week-navigation">
                <button onClick={() => handleWeekChange(-1)}>Previous Week</button>
                <h2>{calculateWeekRange(currentWeek)}</h2>
                <button onClick={() => handleWeekChange(1)}>Next Week</button>
            </div>
            <table className="availability-table">
                <thead>
                    <tr>
                        <th>Shift</th>
                        {daysOfWeek.map((day) => (
                            <th key={day}>{day}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {shifts.map((shift) => (
                        <tr key={shift}>
                            <td>{shift}</td>
                            {daysOfWeek.map((day) => (
                                <td key={day}>
                                    <input
                                        type="checkbox"
                                        checked={availability[shift][day]}
                                        onChange={() => handleAvailabilityChange(shift, day)}
                                    />
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="submit-button" onClick={handleSubmit}>
                Submit
            </button>
        </div>
    );
};

export default AvailabilityScreen;
