// React & State
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Icons
import { FaEnvelope, FaLock, FaBuilding } from 'react-icons/fa';

// Services
import { login } from '../services/authService';

// Components
import Loader from './Common/Loader';
import BackgroundWrapper from './Layouts/BackgroundWrapper';

// Styles
import '../styles/LoginScreen.css';

function LoginScreen() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        companyName: '',
    });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        window.scrollTo({ top: 0 });
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setIsLoading(true); // התחלת טעינה
        try {
            const response = await login(formData);
            if (response.success) {
                const userWithCompany = {
                    user: response.user,
                    company: response.company,
                };
                localStorage.setItem('user', JSON.stringify(userWithCompany));
                navigate('/home', { state: { user: response.user, company: response.company } });
            } else {
                setError(response.message || 'Login failed. Please try again.');
            }
        } catch (err) {
            setError('An error occurred. Please try again later.');
        } finally {
            setIsLoading(false); // סיום טעינה
        }
    };

    if (isLoading) return <Loader />;

    return (
        <BackgroundWrapper>

            <div className="login-container">
                <div className="login-card">
                    <h1>Welcome Back</h1>
                    <p className="subtitle">Please login to continue</p>
                    <form onSubmit={handleLogin}>
                        <div className="form-group">
                            <label htmlFor="companyName">Company Name</label>
                            <div className="input-wrapper">
                                <FaBuilding className="login-input-icon" />
                                <input
                                    type="text"
                                    id="companyName"
                                    name="companyName"
                                    placeholder="Enter your company name"
                                    value={formData.companyName}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <div className="input-wrapper">
                                <FaEnvelope className="login-input-icon" />
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    placeholder="Enter your email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <div className="input-wrapper">
                                <FaLock className="login-input-icon" />
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    placeholder="Enter your password"
                                    value={formData.password}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>
                        {error && <p className="error">{error}</p>}
                        <button type="submit" className="primary-button">Login</button>
                    </form>
                    <p className="signup-prompt">
                        Don't have an account?{' '}
                        <button
                            type="button"
                            className="link-button"
                            onClick={() => navigate('/signup')}
                        >
                            Sign up
                        </button>
                    </p>
                </div>
            </div>
        </BackgroundWrapper>
    );
}

export default LoginScreen;
