import React, { useState, useEffect } from 'react';
import { getAllAvailabilities } from '../services/availabilityService';
import { getUsers } from '../services/userService'; // ייבוא פונקציית שליפת משתמשים
import Navbar from '../components/Navbar';
import '../styles/ScheduleScreen.css';

function ScheduleScreen() {
    const [availability, setAvailability] = useState({});
    const [users, setUsers] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [weekKey, setWeekKey] = useState('week_2025_01_19'); // Default week key
    const [currentCompany, setCurrentCompany] = useState(null);

    useEffect(() => {
        const fetchAvailabilityAndUsers = async () => {
            try {
                const loggedUser = JSON.parse(localStorage.getItem('user'));
                const companyId = loggedUser?.company?.id;

                if (!companyId) {
                    setError('No company information found.');
                    return;
                }

                setCurrentCompany(companyId);

                // Fetch availability and users
                const [availabilityData, usersData] = await Promise.all([
                    getAllAvailabilities(companyId, weekKey),
                    getUsers()
                ]);

                setAvailability(availabilityData || {});
                setUsers(usersData || {});
            } catch (err) {
                setError('Failed to fetch data.');
                console.error(err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchAvailabilityAndUsers();
    }, [weekKey]);

    const handleWeekChange = (direction) => {
        const [prefix, year, month, day] = weekKey.split('_');
        const date = new Date(`${year}-${month}-${day}`);
        date.setDate(date.getDate() + (direction === 'next' ? 7 : -7));

        const newWeekKey = `week_${date.getFullYear()}_${String(date.getMonth() + 1).padStart(2, '0')}_${String(
            date.getDate()
        ).padStart(2, '0')}`;
        setWeekKey(newWeekKey);
    };

    const calculateWeekRange = (weekKey) => {
        const [prefix, year, month, day] = weekKey.split('_');
        const startDate = new Date(`${year}-${month}-${day}`);
        const endDate = new Date(startDate);
        endDate.setDate(startDate.getDate() + 6);

        const formatDate = (date) => {
            const day = String(date.getDate()).padStart(2, '0');
            const month = String(date.getMonth() + 1).padStart(2, '0');
            const year = date.getFullYear();
            return `${day}/${month}/${year}`;
        };

        return `${formatDate(startDate)} - ${formatDate(endDate)}`;
    };

    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div> {/* Add placeholder for Navbar */}

            <div className="schedule-container">
                <h2>Weekly Schedule</h2>
                <div className="week-navigation">
                    <button onClick={() => handleWeekChange('prev')}>Previous Week</button>
                    <span className="week-range">{calculateWeekRange(weekKey)}</span>
                    <button onClick={() => handleWeekChange('next')}>Next Week</button>
                </div>

                {isLoading ? (
                    <p>Loading schedule...</p>
                ) : error ? (
                    <p className="error">{error}</p>
                ) : (
                    <table className="schedule-table">
                        <thead>
                            <tr>
                                <th>Shift</th>
                                {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map(
                                    (day) => (
                                        <th key={day}>{day}</th>
                                    )
                                )}
                            </tr>
                        </thead>
                        <tbody>
                            {['Morning', 'Noon', 'Evening', 'Night'].map((shift) => (
                                <tr key={shift}>
                                    <td>{shift}</td>
                                    {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map(
                                        (day) => (
                                            <td key={day}>
                                                {Object.entries(availability).map(([userId, userData]) => {
                                                    const isAvailable =
                                                        userData?.[shift]?.[day] === true;
                                                    return isAvailable ? (
                                                        <div key={userId}>
                                                            {users[userId]?.name || 'Unknown'}
                                                        </div>
                                                    ) : null;
                                                })}
                                            </td>
                                        )
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default ScheduleScreen;
