import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/HomeScreen.css'; // ייבוא קובץ הסגנונות

function HomeScreen() {
    const location = useLocation();
    const navigate = useNavigate();
    const { user, company } = location.state || {}; // קבלת פרטי המשתמש והחברה

    const handleLogout = () => {
        // בוצע התנתקות (אפשר לממש את הפונקציה הזו בהמשך)
        navigate('/'); // חזרה למסך ההתחברות
    };

    // הצגת הודעה אם אין נתונים
    if (!user || !company) {
        return <p>Loading...</p>; // תצוגת טעינה עד שהנתונים נטענים
    }

    return (
        <div className="home-container">
            <h1>Welcome, {user.name}!</h1>
            <p>Company: {company.name}</p>
            <div className="home-actions">
                <button onClick={handleLogout}>Logout</button>
                {/* כל כפתור נוסף שתצטרך לשים */}
            </div>
        </div>
    );
}

export default HomeScreen;
