import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../styles/AvailabilityScreen.css';
import '../styles/Navbar.css';
import { saveAvailability, getAvailability, saveNecessaryEmployees, getNecessaryEmployees, fetchEditStatus } from '../services/availabilityService';
import { getUsers } from '../services/userService';
import { getWeekDateObjects, calculateWeekKey, calculateWeekRange } from '../utils/utils';

import Select from 'react-select'; // ודא שזה מופיע בתחילת הקובץ

const AvailabilityScreen = () => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const shifts = ['Morning', 'Noon', 'Evening', 'Night'];

    const [employees, setEmployees] = useState([]);
    const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);
    const [availability, setAvailability] = useState(null);
    const [notes, setNotes] = useState('');
    const [currentWeek, setCurrentWeek] = useState(1);
    const [userData, setUserData] = useState(null);
    const [weekOffset, setWeekOffset] = useState(0);
    const [sortConfig, setSortConfig] = useState({ key: 'name', direction: 'asc' });
    const [isEditAllowed, setIsEditAllowed] = useState(true);

    const navigate = useNavigate();

    const [weekDays, setWeekDays] = useState(getWeekDateObjects(weekOffset));

    useEffect(() => {
        const fetchEditStatusForWeek = async () => {
            if (!userData) return;

            const companyId = userData.company.id;
            const weekKey = calculateWeekKey(currentWeek);

            try {
                const status = await fetchEditStatus(companyId, weekKey);
                setIsEditAllowed(status?.isEditAllowed ?? false);
            } catch (error) {
                console.error('Failed to fetch edit status:', error);
                setIsEditAllowed(false);
            }
        };

        fetchEditStatusForWeek();
    }, [userData, currentWeek]);


    // On component mount, retrieve the logged-in user from localStorage.
    // If no user is found, redirect to the login page.
    // If the user is an admin, fetch all employees associated with the same company.
    // Otherwise, set the selected employee ID to the current user's ID.
    useEffect(() => {
        const loggedUser = JSON.parse(localStorage.getItem('user'));
        if (!loggedUser) {
            navigate('/');
        } else {
            setUserData(loggedUser);

            if (loggedUser.user.role === 'admin') {
                const fetchEmployees = async () => {
                    try {
                        const usersData = await getUsers();
                        const companyId = loggedUser.company.id;

                        if (companyId) {
                            const filteredEmployees = Object.entries(usersData)
                                .map(([id, user]) => ({ id, ...user }))
                                .filter((user) =>
                                    user.companyIds?.some((company) => company.companyId === companyId)
                                );

                            setEmployees(filteredEmployees);
                        }
                    } catch (error) {
                        console.error('Error fetching employees:', error);
                    }
                };

                fetchEmployees();
            } else {
                setSelectedEmployeeId(loggedUser.user.id);
            }
        }
    }, [navigate]);

    // Whenever the current week, selected employee, or user data changes,
    // fetch the availability data for the selected employee for the relevant week.
    // Initialize the availability state and notes based on the fetched data.
    useEffect(() => {
        const fetchAvailability = async () => {
            if (!selectedEmployeeId || !userData) return;

            const companyId = userData.company.id;
            const weekKey = calculateWeekKey(currentWeek);

            try {
                const fetchedAvailability = await getAvailability(companyId, weekKey, selectedEmployeeId);
                setAvailability(initializeAvailability(fetchedAvailability || {}));
                setNotes(fetchedAvailability?.notes || '');
            } catch (error) {
                console.error('Error fetching availability:', error);
                alert('Failed to fetch availability. Please try again.');
            }
        };

        fetchAvailability();
    }, [currentWeek, selectedEmployeeId, userData]);

    // Update the current week number whenever the week offset changes.
    // Week numbers are 1-based, so we add 1 to the offset.

    useEffect(() => {
        setCurrentWeek(weekOffset + 1);
        setWeekDays(getWeekDateObjects(weekOffset));
    }, [weekOffset]);

    // Initialize the availability structure for all shifts and days of the week.
    // If existing availability data is provided, use it;
    // otherwise, default to isAvailable: false and status: 'default'.
    const initializeAvailability = (existingAvailability) => {
        return shifts.reduce((acc, shift) => {
            acc[shift] = daysOfWeek.reduce((dayAcc, day) => {
                dayAcc[day] = {
                    isAvailable: existingAvailability[shift]?.[day]?.isAvailable || false,
                    status: existingAvailability[shift]?.[day]?.status || 'default'
                };
                return dayAcc;
            }, {});
            return acc;
        }, {});
    };

    // Toggle the availability status for a specific shift and day.
    const handleAvailabilityChange = (shift, day) => {
        setAvailability((prev) => ({
            ...prev,
            [shift]: {
                ...prev[shift],
                [day]: {
                    ...prev[shift][day],
                    isAvailable: !prev[shift][day].isAvailable,
                },
            },
        }));
    };

    // Toggle all shifts for a specific day column.
    // If all are selected, unselect them; otherwise, select all.
    const handleDayColumnClick = (day) => {
        setAvailability((prev) => {
            const updatedAvailability = JSON.parse(JSON.stringify(prev));
            const allSelected = shifts.every((shift) => updatedAvailability[shift]?.[day]?.isAvailable);

            shifts.forEach((shift) => {
                updatedAvailability[shift][day].isAvailable = !allSelected;
            });

            return updatedAvailability;
        });
    };

    // Toggle all days for a specific shift row.
    // If all are selected, unselect them; otherwise, select all.
    const handleShiftRowClick = (shift) => {
        setAvailability((prev) => {
            const updatedAvailability = JSON.parse(JSON.stringify(prev));
            const allSelected = daysOfWeek.every((day) => updatedAvailability[shift]?.[day]?.isAvailable);

            daysOfWeek.forEach((day) => {
                updatedAvailability[shift][day].isAvailable = !allSelected;
            });

            return updatedAvailability;
        });
    };

    // Toggle all checkboxes in the entire table.
    // If everything is selected, unselect all; otherwise, select all.
    const handleSelectAll = () => {
        setAvailability((prev) => {
            const updatedAvailability = JSON.parse(JSON.stringify(prev));
            const allSelected = shifts.every((shift) =>
                daysOfWeek.every((day) => updatedAvailability[shift][day].isAvailable)
            );

            shifts.forEach((shift) => {
                daysOfWeek.forEach((day) => {
                    updatedAvailability[shift][day].isAvailable = !allSelected;
                });
            });

            return updatedAvailability;
        });
    };

    // Handle submission of availability data for the selected employee.
    // 1. Show error if no employee is selected.
    // 2. Check if editing is allowed for the current week.
    // 3. Save the availability data along with any notes.
    // 4. If no necessaryEmployees data exists for the week, initialize it with default (0) values for each shift per day.
    // 5. Save necessaryEmployees data only if it was just initialized.
    const handleSubmit = async () => {
        if (!selectedEmployeeId) {
            toast.error('Please select an employee to save availability.');
            return;
        }

        const companyId = userData.company.id;
        const weekKey = calculateWeekKey(currentWeek);

        try {
            // בדיקת סטטוס עריכה עבור השבוע
            const editStatus = await fetchEditStatus(companyId, weekKey);
            if (!editStatus?.isEditAllowed) {
                toast.error('Editing availability is currently locked for this week.');
                return;
            }

            // שמירת זמינות של המשתמש
            const dataToSave = {
                ...availability,
                notes,
            };
            await saveAvailability(companyId, weekKey, selectedEmployeeId, dataToSave);

            // בדיקה אם יש כבר נתונים של necessaryEmployees ב-DB
            let necessaryEmployees = await getNecessaryEmployees(companyId, weekKey);

            // אם אין נתונים ב-DB, לאתחל אותם
            if (!necessaryEmployees || Object.keys(necessaryEmployees).length === 0) {
                necessaryEmployees = daysOfWeek.reduce((acc, day) => {
                    acc[day] = {
                        Morning: 0,
                        Noon: 0,
                        Evening: 0,
                        Night: 0,
                    };
                    return acc;
                }, {});

                // שמירה ל-DB רק במקרה של אתחול
                await saveNecessaryEmployees(companyId, weekKey, necessaryEmployees);
            }

            toast.success('Availability and notes saved successfully!');
        } catch (error) {
            console.error('Error saving availability or necessary employees:', error);
            toast.error('Failed to save data. Please try again.');
        }
    };

    // Adjust the week offset based on the given direction.
    // Positive value moves to a future week, negative to a past week.
    const handleWeekChange = (direction) => {
        setWeekOffset((prevOffset) => prevOffset + direction);
    };

    if (!userData) {
        return <p>Loading...</p>;
    }

    // Sort the employees list based on the selected key and direction (ascending or descending).
    // Uses the current sortConfig to determine sort behavior.
    const sortedEmployees = [...employees].sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
            return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
            return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
    });

    // Update the sorting configuration when a column header is clicked.
    // If the same key is clicked again, toggle between ascending and descending.
    const handleSort = (key) => {
        setSortConfig((prev) => ({
            key,
            direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc',
        }));
    };

    // Render the availability screen UI:
    // - Show the Navbar and a placeholder to prevent layout shift.
    // - If the user is an admin, show a dropdown to select an employee.
    // - If an employee is selected, display:
    //   1. Week navigation controls (previous/next).
    //   2. A table for selecting availability per shift and day.
    //   3. A notes textarea for weekly notes.
    //   4. A submit button to save availability and notes.
    // - Display toast notifications using <ToastContainer />.
    return (
        <div>
            <Navbar />
            <div className="navbar-placeholder"></div>

            <div className="availability-container">
                <h1>Weekly Availability</h1>
                {userData.user.role === 'admin' && (
                    <div className="employee-selector">
                        {/* Select dropdown */}
                        <div className="dropdown-container">
                            <label htmlFor="employeeDropdown">Select Employee:</label>
                            <Select
                                className="employee-dropdown"
                                classNamePrefix="react-select"
                                options={sortedEmployees.map((e) => ({ value: e.id, label: e.name }))}
                                onChange={(option) => setSelectedEmployeeId(option?.value || '')}
                                placeholder="Select an employee"
                                menuPlacement="bottom"
                                value={
                                    sortedEmployees
                                        .map((e) => ({ value: e.id, label: e.name }))
                                        .find((o) => o.value === selectedEmployeeId) || null
                                }
                            />
                        </div>

                        {/* Employee photo */}
                        {selectedEmployeeId && (() => {
                            const selected = employees.find(emp => emp.id === selectedEmployeeId);
                            return selected?.photoData ? (
                                <img
                                    src={selected.photoData}
                                    alt={`${selected.name}'s profile`}
                                    className="employee-photo-preview"
                                />
                            ) : null;
                        })()}
                    </div>

                )}

                {selectedEmployeeId && (
                    <>
                        <div className="week-navigation">
                            <button className="navigation-button" onClick={() => handleWeekChange(-1)}>
                                Previous Week
                            </button>
                            <h2 className="week-range">{calculateWeekRange(currentWeek)}</h2>
                            <button className="navigation-button" onClick={() => handleWeekChange(1)}>
                                Next Week
                            </button>
                        </div>
                        <table className="availability-table">
                            <thead>
                                <tr>
                                    <th onClick={handleSelectAll} className="clickable">Select All</th>
                                    {weekDays.map(({ name, date }) => (
                                        <th key={name} onClick={() => handleDayColumnClick(name)} className="clickable">
                                            {name}
                                            <br />
                                            <span style={{ fontSize: '0.85em', color: '#555' }}>{date}</span>
                                        </th>
                                    ))}

                                </tr>
                            </thead>
                            <tbody>
                                {shifts.map((shift) => (
                                    <tr key={shift}>
                                        <td onClick={() => handleShiftRowClick(shift)} className="clickable">
                                            {shift}
                                        </td>
                                        {daysOfWeek.map((day) => (
                                            <td key={day}>
                                                <input
                                                    type="checkbox"
                                                    checked={availability?.[shift]?.[day]?.isAvailable || false}
                                                    onChange={() => handleAvailabilityChange(shift, day)}
                                                />
                                            </td>
                                        ))}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <textarea
                            className="notes-textarea"
                            placeholder="Write weekly notes here..."
                            value={notes}
                            onChange={(e) => setNotes(e.target.value)}
                        ></textarea>
                        <p style={{ textAlign: 'center', color: isEditAllowed ? 'green' : 'red', fontWeight: 'bold' }}>
                            {isEditAllowed ? 'Availability editing is currently open.' : 'Availability editing is currently locked.'}
                        </p>

                        <button className="submit-button" onClick={handleSubmit}>
                            Submit
                        </button>
                    </>
                )}
            </div>

            <ToastContainer />
        </div>
    );
};

export default AvailabilityScreen;
